import { Component, OnInit } from '@angular/core';
import { FieldType, FieldTypeConfig } from '@ngx-formly/core';
import { InputNumberModule } from 'primeng/inputnumber';

@Component({
  selector: 'n2-formly-textarea-new',
  template: `
    <textarea [formControl]="formControl" [formlyAttributes]="field" pInputTextarea [rows]="5" [cols]="100"></textarea>
  `,
})
export class FormlyTextAreaNewComponent extends FieldType<FieldTypeConfig> { }
