import {Component, Inject, LOCALE_ID, OnInit, ViewChild} from '@angular/core';
import {IInstalmentPlan} from 'app/payment/instalment/model/instalment-plan.model';
import {CustomToastService} from 'app/layouts/service/customtoast.service';
import {NspConfirmationService} from 'app/shared/confirm/nsp-confirm.service';
import {InstalmentService} from 'app/payment/instalment/service/instalment.service';
import {HttpErrorResponse, HttpResponse} from '@angular/common/http';
import {InstalmentTerminateComponent} from 'app/payment/instalment/instalment-terminate/instalment-terminate.component';
import {Table} from "primeng/table";
import {Ns2HttpService} from "app/shared/service/ns2-http.service";
import {FilterMatchMode, FilterMetadata, LazyLoadEvent} from "primeng/api";
import {N2Status} from 'app/shared/model/ns2status-enum.model';
import { Ns2Status } from 'app/shared/constants/nspapp.constants';
import { IcCodeService } from 'app/ng-connect';
import { formatDate } from '@angular/common';
import { ExportUtilsService } from 'app/core/util/export-utils.service';
import { DateUtilsService } from 'app/core/util/date-utils.service';

@Component({
  selector: 'n2-instalment',
  templateUrl: './instalment.component.html',
  styleUrls: ['./instalment.component.scss']
})
export class InstalmentComponent implements OnInit {

  //Filters
  instalmentStatusOptions: any[];
  instalmentTerRsnOptions: any[];

  instalmentList: IInstalmentPlan[] = [];
  displayTerminateInstalmentDialog: any = { display: false };

  // Enumeration object for decoding common NSPAY2.0 statuses
  n2StatusEnum: { [key: string]: N2Status } = N2Status

  // Lazy loading
  selectableRecords: number;
  loading = false;
  totalRecords = 0;
  pageData: { currentPage: number } = {
    currentPage: 0
  }
  pageSize = 10;
  lazyLoadEvent: LazyLoadEvent;

  // Selection checkbox handling
  selectAll = false;
  selectedInstalmentList: IInstalmentPlan[] = [];
  selectableRowsExist = false;

  @ViewChild('dt') dataTable: Table;

  @ViewChild('instalmentTerminateComponent')
  private instalmentTerminateComponent: InstalmentTerminateComponent;

  constructor(
    private instalmentService: InstalmentService,
    private toastService: CustomToastService,
    private nspConfirmService: NspConfirmationService,
    private n2httpService: Ns2HttpService,
    private codeService: IcCodeService,
    private exportUtilsService: ExportUtilsService,
    private dateUtilsService: DateUtilsService,
    @Inject(LOCALE_ID) protected localeId: string
  ) {
  }

  ngOnInit(): void {
  
    this.loadCodes();
  }
  

  loadCodes(): void {
    this.codeService.gets('instalmentStatus, instalmentTerminateRsn').subscribe((codes: any[]) => {
      this.instalmentStatusOptions = codes[0];
      this.instalmentTerRsnOptions = codes[1];

    });
  }


  loadInstalments(event: LazyLoadEvent): void {
    this.loading = true;
    this.lazyLoadEvent = event;

    setTimeout(() => {
      const options = this.n2httpService.convertLazyEvent(event, this.pageData, this.pageSize);
      
      this.instalmentService.getAll(options).subscribe(
        (res: HttpResponse<any>) => {
          if (res.body?.data?.content) {
            this.instalmentList = res.body.data.content;
            this.selectableRecords = this.instalmentList.filter(value => this.n2StatusEnum[value.status] === N2Status.ONGOING).length;
            this.selectableRowsExist = this.selectableRecords > 0;
            this.totalRecords = res.body.data.totalElements;
          } else {
            this.instalmentList = [];
            this.totalRecords = 0;
            this.selectableRowsExist = false;
            // this.toastService.info('No records found');
          }
          this.loading = false;
        },
        (error: HttpErrorResponse) => {
          // eslint-disable-next-line no-console
          console.log(error.message);
        }
      );
    }, 1000);
  }

  onSelectionChange(value: any[] = []): void {
    this.selectAll = value.length === this.selectableRecords;
    this.selectedInstalmentList = value;
  }

  onSelectAllChange(event: any): void {
    const checked = event.checked;
    if (checked) {
      this.selectedInstalmentList = this.instalmentList.filter(value => this.n2StatusEnum[value.status] === N2Status.ONGOING );
      this.selectAll = true;
    } else {
      this.selectedInstalmentList = [];
      this.selectAll = false;
    }
  }

  onPage(event: any): void {
    this.pageSize = event.rows;
    this.pageData.currentPage = event.first / event.rows;
    // Reset selection when loading another page
    this.selectedInstalmentList = [];
    this.selectAll = false;
  }

  exportCSV(dataTable: Table): void {
    const options = this.n2httpService.convertLazyEvent(this.lazyLoadEvent, 0, this.totalRecords);

    this.instalmentService.getAll(options).subscribe(
      resp => {
        if(resp.body?.data?.content) {

          const data = resp.body?.data?.content;

          const columns = 
          [
            {field: 'sn', header: 'SN', index: 0},
            {field: 'nric', header: 'Status', index: 1},
            {field: 'createdBy', header: 'Transaction Id', index: 2},
            {field: 'terminateBy', header: 'Terminated By', index: 3},
            {field: 'terminateApprovedBy', header: 'Termination Approved By', index: 4},
            {field: 'terminateReason', header: 'Termination Reason', index: 5},
            {field: 'status', header: 'Status', index: 6},
            {field: 'lastRefundDate', header: 'Last Instalment Refund', index: 7},
          ];

          const dtoList:any[] = [];
          console.log("data", data);
          data.forEach((d: IInstalmentPlan, index: number) => {
            const dto = {
              sn: index + 1,
              nric: `${String(d.nric)} ${String(d.name)}`, 
              createdBy: `${String(d.submittedBy ? d.submittedBy : "")} ${String(this.dateUtilsService.formatStrDate(d.submittedDt, "DD-MM-YYYY (HHmm)"))}`,
              terminateBy: `${String(d.terminationRequestedBy ? d.terminationRequestedBy : "")} ${String(this.dateUtilsService.formatStrDate(d.terminationRequestedDt, "DD-MM-YYYY (HHmm)"))}`,
              terminateApprovedBy: `${String(d.terminationReviewedBy ? d.terminationReviewedBy : "")} ${String(this.dateUtilsService.formatStrDate(d.terminationReviewedDt, "DD-MM-YYYY (HHmm)"))}`,
              terminateReason: d.reason,
              status: this.n2StatusEnum[d.status],
              lastRefundDate: d.lastRefundDate
            }
            dtoList.push(dto);
          });
          this.exportUtilsService.exportCSV(dataTable, dtoList,resp.body.data.total_no_of_elems, columns, 
            "data_".concat(formatDate(new Date(),"dd-MM-YYYY", this.localeId)));
        }
      },
      error => {
        console.log(error.error?.error_desc);
        this.loading = false;
      })
  }

  onOpenTerminate(dialog: any): void {
    // Populate terminate dialog's data table
    // console.log(this.selectedInstalments);
    // this.instalmentTerminateComponent.instalmentRecordsToTerminate = this.selectedInstalmentList;
    this.instalmentTerminateComponent.setSelectedRecords(this.selectedInstalmentList);
    dialog.display = true;
  }

  confirmTerminateSelection(event: Event, key: string, dialog: any): void {
    this.nspConfirmService.onConfirmPopup(event, key, dialog, () => {
      this.terminateSelection();
    });
  }

  terminateSelection(): void {
    this.selectedInstalmentList.forEach((instalment:IInstalmentPlan)=>{
      instalment.reason = this.instalmentTerminateComponent.terminateInstalmentForm.get('reason').value;
    });
    
    this.instalmentService.terminate(this.selectedInstalmentList).subscribe(
      (res: HttpResponse<any>) => {
        if (res.body?.data) {
          this.toastService.success(res.body?.success_message);
          this.resetDataTable();
        }
      },
      (error: HttpErrorResponse) => {
        console.log(error.message);
        this.toastService.error(error.error?.error_desc as string);
        this.resetDataTable();
      });
  }

  resetDataTable(): void {
    this.selectAll = false;
    this.selectedInstalmentList = [];
    this.dataTable.filters = {};
    this.pageData.currentPage = 0;
    this.pageSize = 10;
    this.dataTable.reset();
  }

  timestampCallbackFilter(input: any, filter: (a: any) => void, id: string, table: Table): void {
    filter(new Date(input).toISOString());

    //Patch back value without triggering filter
    const filterMetadata = table.filters[id] as FilterMetadata[];
    filterMetadata[0].value = input;
  }

  dateCallbackFilter(input: any, filter: (a: any) => void, id: string, table: Table): void {
    const formatted: string = formatDate(input, "yyyy-MM-dd", this.localeId).toString();
    filter(formatted);

    setTimeout(() => {
      const filterMetadata = table.filters[id] as FilterMetadata[];
      filterMetadata[0].value = input;
    }, 1000);
  }

}
<p-table #dt [value]='instalmentList' [(selection)]="selectedInstalmentList"
  (selectionChange)="onSelectionChange($event)" [selectAll]="selectAll" (selectAllChange)="onSelectAllChange($event)"
  styleClass='p-datatable-gridlines' [paginator]='true' [rows]='10'
  currentPageReportTemplate='Showing {first} to {last} of {totalRecords} entries' [rowsPerPageOptions]='[10,25,50]'
  [showCurrentPageReport]='true' [lazy]='true' [totalRecords]='totalRecords' [loading]='loading'
  (onLazyLoad)='loadInstalments($event)' (onPage)="onPage($event)">
  <ng-template pTemplate='caption'>
    <div class='flex justify-content-between'>
      <button type='button' pButton pRipple icon='pi pi-file' (click)='exportCSV(dt)' pTooltip='Export as CSV'
        tooltipPosition='bottom'></button>
      <button type='button' pButton label='Terminate' (click)='onOpenTerminate(displayTerminateInstalmentDialog)'
        pTooltip='Terminate Instalment Records' tooltipPosition='bottom'
        [disabled]="selectedInstalmentList?.length === 0"></button>
    </div>
    <p-confirmPopup key='terminateInstalmentRecords'></p-confirmPopup>
  </ng-template>
  <ng-template pTemplate='header'>
    <tr>
      <th scope="col" style="width:3%">
        <p-tableHeaderCheckbox *ngIf="selectableRowsExist"></p-tableHeaderCheckbox>
      </th>
      <th scope="col" style='width: 5%'>SN</th>
      <th scope="col" pSortableColumn='nric'>Name<p-sortIcon field='nric'></p-sortIcon>
        <p-columnFilter type="text" field="nric" display="menu" matchMode="contains"></p-columnFilter>
      </th>
      <th scope="col" pSortableColumn='submittedDt'>Created By<p-sortIcon field='createdBy'></p-sortIcon>
        <p-columnFilter field="submittedDt" display="menu" matchMode="contains" [showApplyButton]="false">
          <ng-template pTemplate="filter" let-value let-filter="filterCallback">
            <p-calendar [ngModel]="value" (onSelect)="timestampCallbackFilter($event, filter, 'submittedDt', dt)"
              dateFormat="dd-mm-yy">
            </p-calendar>
          </ng-template>
        </p-columnFilter>
      </th>
      <th scope="col" pSortableColumn='terminationRequestedDt'>Terminated By<p-sortIcon field='terminationRequestedDt'></p-sortIcon>
        <p-columnFilter field="terminationRequestedDt" display="menu" matchMode="contains" [showApplyButton]="false">
          <ng-template pTemplate="filter" let-value let-filter="filterCallback">
            <p-calendar [ngModel]="value"
              (onSelect)="timestampCallbackFilter($event, filter, 'terminationRequestedDt', dt)" dateFormat="dd-mm-yy">
            </p-calendar>
          </ng-template>
        </p-columnFilter>
      </th>
      <th scope="col" pSortableColumn='terminationReviewedDt'>Termination Approved By<p-sortIcon field='terminationReviewedDt'>
        </p-sortIcon>
        <p-columnFilter field="terminationReviewedDt" display="menu" matchMode="contains" [showApplyButton]="false">
          <ng-template pTemplate="filter" let-value let-filter="filterCallback">
            <p-calendar [ngModel]="value"
              (onSelect)="timestampCallbackFilter($event, filter, 'terminationReviewedDt', dt)" dateFormat="dd-mm-yy">
            </p-calendar>
          </ng-template>
        </p-columnFilter>
      </th>
      <th scope="col" pSortableColumn='reason'>Termination Reason<p-sortIcon field='reason'></p-sortIcon>
        <p-columnFilter field="reason" display="menu" matchMode="contains" [showApplyButton]="false">
          <ng-template pTemplate="filter" let-value let-filter="filterCallback">
            <p-dropdown [ngModel]="value" [options]="instalmentTerRsnOptions" placeholder="Select a Status"
              (onChange)="filter($event.value)" optionLabel="label.en-US" optionValue="codeId"></p-dropdown>
          </ng-template>
        </p-columnFilter>
      </th>
      <th scope="col" pSortableColumn='status'>Status<p-sortIcon field='status'></p-sortIcon>
        <p-columnFilter field="status" display="menu" matchMode="contains" [showApplyButton]="false">
          <ng-template pTemplate="filter" let-value let-filter="filterCallback">
            <p-dropdown [ngModel]="value" [options]="instalmentStatusOptions" placeholder="Select a Status"
              (onChange)="filter($event.value)" optionLabel="label.en-US" optionValue="codeId"></p-dropdown>
          </ng-template>
        </p-columnFilter>
      </th>
      <th scope="col" pSortableColumn='lastRefundDate'>Last Instalment Refund<p-sortIcon field='lastRefundDate'></p-sortIcon>
        <p-columnFilter field="lastRefundDate" display="menu" matchMode="contains" [showApplyButton]="false">
          <ng-template pTemplate="filter" let-value let-filter="filterCallback">
            <p-calendar [ngModel]="value" (onSelect)="dateCallbackFilter($event, filter, 'lastRefundDate', dt)"
              dateFormat="dd-mm-yy">
            </p-calendar>
          </ng-template>
        </p-columnFilter>
      </th>
    </tr>
  </ng-template>
  <ng-template pTemplate='body' let-instalment let-i='rowIndex'>
    <tr>
      <td>
        <p-tableCheckbox *ngIf="instalment?.status === 'ONGOING'" [value]="instalment"></p-tableCheckbox>
      </td>
      <td><a [routerLink]="['/payment/instalment/', instalment.id]">{{i + 1}}</a></td>
      <td>{{instalment.nric | defaultString}} {{instalment.name | defaultString}}</td>
      <td>{{instalment.submittedBy}} <br /> {{instalment.submittedDt | date:'dd-MM-yyyy \'(\'HHmm\')\''}}</td>
      <td>{{instalment.terminationRequestedBy | defaultString }} <br /> {{instalment.terminationRequestedDt |
        date:'dd-MM-yyyy \'(\'HHmm\')\''}}</td>
      <td>{{instalment.terminationReviewedBy | defaultString }} <br /> {{instalment.terminationReviewedDt |
        date:'dd-MM-yyyy \'(\'HHmm\')\''}}</td>
      <td>{{instalment.reason | defaultString}}</td>
      <td>{{n2StatusEnum[instalment.status]| defaultString}}</td>
      <td>{{instalment.lastRefundDate | defaultString}}</td>
    </tr>
  </ng-template>

  <ng-template pTemplate='emptymessage'>
    <tr>
      <td colspan='9'>No Instalment records found.</td>
    </tr>
  </ng-template>
</p-table>


