import { Component, OnInit, OnDestroy } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { HttpResponse } from '@angular/common/http';
import { Subscription, Observable } from 'rxjs';
import { AlertService } from 'app/core/util/alert.service';
import { EventManager } from 'app/core/util/event-manager.service';
import { Resource } from '../resource/resource.model';
import { UserService } from './user.service';
import { Group } from '../group/group.model';
import { Subject } from './user.model';
import { SubjectLogin } from './user-login.model';
import { DATE_FORMAT } from 'app/config/input.constants';
import { validateNRIC } from 'app/ng-connect/nric/nric-validate';

@Component({
  selector: 'ic-user-edit',
  templateUrl: './user-edit.component.html',
})
export class UserEditComponent implements OnInit, OnDestroy {
  currentAccount: any;
  error: any;
  success: any;
  eventSubscriber?: Subscription;
  links: any;
  totalItems: any;
  queryCount: any;
  itemsPerPage: any;
  page: any;
  predicate!: string;
  reverse: any;

  isSaving?: boolean;
  subjectId: string | undefined;
  user: Subject = new Subject();
  userLogin: SubjectLogin;
  userLogins: SubjectLogin[];
  initialLoginName: string;

  assignedRoles: Resource[] = [];
  assignedGroups: Group[] = [];

  selectedAllRoles?: boolean;
  selectedRoles: Resource[] = [];

  selectedAllGroups?: boolean;
  selectedGroups: Group[] = [];

  subscription?: Subscription;

  effectiveDtDp: any;
  expiryDtDp: any;
  statuses: any[];
  loginTypes: any[];

  salutations: string[];
  invalidNric = false;

  constructor(
    private userService: UserService,
    private alertService: AlertService,
    private route: ActivatedRoute,
    private eventManager: EventManager
  ) {
    this.userLogin = new SubjectLogin();
    this.userLogins = [];
    this.statuses = [
      { codeId: 'A', codeDesc: 'Active' },
      { codeId: 'S', codeDesc: 'Suspended' },
      { codeId: 'I', codeDesc: 'Inactive' },
      { codeId: 'R', codeDesc: 'Revoked' },
      { codeId: 'P', codeDesc: 'Pending' },
      { codeId: 'D', codeDesc: 'Deactived' },
    ];
    this.loginTypes = [
      { codeId: 'PASSWORD', codeDesc: 'Password' },
      { codeId: 'SINGPASS', codeDesc: 'Singpass' },
      { codeId: 'CORPPASS', codeDesc: 'CorpPass' },
      { codeId: 'ADFS', codeDesc: 'ADFS' },
      { codeId: 'LDAP', codeDesc: 'LDAP' },
      { codeId: 'OAUTH2', codeDesc: 'Oauth2' },
    ];
    this.salutations = ['Mr', 'Ms', 'Mdm', 'Dr'];
  }

  ngOnInit(): void {
    this.isSaving = false;
    this.subscription = this.route.params.subscribe(params => {
      this.subjectId = params['id'];
    });

    // get user object
    this.load(this.subjectId);

    // get all assigned roles, groups
    this.getAllAssignedRoles(this.subjectId);
    this.getAllAssignedGroups(this.subjectId);

    // register change
    this.registerChangeInAssignedRoles();
    this.registerChangeInAssignedGroups();
  }

  ngOnDestroy(): void {
    if (this.eventSubscriber) {
      this.eventManager.destroy(this.eventSubscriber);
    }
  }

  load(id: any): void {
    this.userService.find(id).subscribe(user => {
      user.effectiveDt = user.effectiveDt ? user.effectiveDt.format(DATE_FORMAT) : null;
      user.expiryDt = user.expiryDt ? user.expiryDt.format(DATE_FORMAT) : null;
      this.user = user;
      this.userLogin = this.user.subjectLogins ? this.user.subjectLogins[0] : new SubjectLogin();
      this.initialLoginName = this.userLogin.loginName;
    });
  }

  getAllAssignedRoles(subjectId: any): void {
    this.userService.findAllAssignedRoles(subjectId).subscribe(
      (res: HttpResponse<any>) => {
        this.assignedRoles = res.body;
        this.selectedAllRoles = false;
        this.selectedRoles = [];
      },
      (res: HttpResponse<any>) => this.onError(res.body)
    );
  }

  getAllAssignedGroups(subjectId: any): void {
    this.userService.findAllAssignedGroups(subjectId).subscribe(
      (res: HttpResponse<any>) => {
        this.assignedGroups = res.body;
        this.selectedAllGroups = false;
        this.selectedGroups = [];
      },
      (res: HttpResponse<any>) => this.onError(res.body)
    );
  }

  onKeyupNric(): void {
    this.invalidNric = false;
    if (this.user.nric) {
      this.isSaving = false;
    }
  };

  save(): void {
    this.isSaving = true;
    if (this.user.nric) {
      const isValid = validateNRIC(this.user.nric);
      if (isValid) {
        // eslint-disable-next-line @typescript-eslint/no-unnecessary-condition
        if (this.userLogin) {
          this.userLogins = [];
          this.userLogins.push(this.userLogin);
          this.user.subjectLogins = this.userLogins;
        }
        this.subscribeToSaveResponse(this.userService.update(this.user));
      } else {
        this.invalidNric = true;
        this.user.nric = '';
      }
    }
  }

  selectAllRoles(event: MouseEvent): void {
    this.selectedRoles = [];
    this.selectedAllRoles = (event.target as HTMLInputElement).checked;
    if (this.selectedAllRoles) {
      this.assignedRoles.forEach(element => {
        this.selectedRoles.push(element);
      });
    }
  }

  selectOneRole(event: MouseEvent, resource: any): void {
    if ((event.target as HTMLInputElement).checked) {
      if (this.selectedRoles.length === 0) {
        this.selectedRoles = [];
      }
      this.selectedRoles.push(resource);
    } else {
      const index = this.selectedRoles.indexOf(resource);
      if (index >= 0) {
        this.selectedRoles.splice(index, 1);
      }
      if (this.selectedRoles.length === 0) {
        this.selectedAllRoles = false;
      }
    }
  }

  selectAllGroups(event: MouseEvent): void {
    this.selectedGroups = [];
    this.selectedAllGroups = (event.target as HTMLInputElement).checked;
    if (this.selectedAllGroups) {
      for (const selectedGroup of this.assignedGroups) {
        this.selectedGroups.push(selectedGroup);
      }
    }
  }

  selectOneGroup(event: MouseEvent, group: any): void {
    if ((event.target as HTMLInputElement).checked) {
      if (this.selectedGroups.length === 0) {
        this.selectedGroups = [];
      }
      this.selectedGroups.push(group);
    } else {
      const index = this.selectedGroups.indexOf(group);
      if (index >= 0) {
        this.selectedGroups.splice(index, 1);
      }
      if (this.selectedGroups.length === 0) {
        this.selectedAllGroups = false;
      }
    }
  }

  assignedRolesLength(): number {
    return this.assignedRoles.length;
  }

  assignedGroupsLength(): number {
    return this.assignedGroups.length;
  }

  unAssignRoles(): void {
    if (this.selectedRoles.length > 0) {
      this.userService.unAssignRoles(this.subjectId, this.selectedRoles).subscribe(() => {
        this.eventManager.broadcast({ name: 'assignedRoleListModification', content: 'OK' });
      });
    }
  }

  unAssignGroups(): void {
    if (this.selectedGroups.length > 0) {
      this.userService.unAssignGroups(this.subjectId, this.selectedGroups).subscribe(() => {
        this.eventManager.broadcast({ name: 'assignedGroupListModification', content: 'OK' });
      });
    }
  }

  registerChangeInAssignedRoles(): void {
    this.eventSubscriber = this.eventManager.subscribe('assignedRoleListModification', () => this.getAllAssignedRoles(this.user.subjectId));
  }

  registerChangeInAssignedGroups(): void {
    this.eventSubscriber = this.eventManager.subscribe('assignedGroupListModification', () =>
      this.getAllAssignedGroups(this.user.subjectId)
    );
  }

  showLoginId(): boolean {
    return true;
  }

  private subscribeToSaveResponse(result: Observable<Group>): void {
    result.subscribe(
      (res: Group) => this.onSaveSuccess(res),
      (res: HttpResponse<any>) => this.onSaveError(res)
    );
  }

  private onSaveSuccess(result: any): void {
    this.isSaving = false;
    this.load(result.subjectId);
  }

  private onSaveError(error: any): void {
    this.isSaving = false;
    const appDialogErrorMsg = error.headers.get('X-nspay2App-alert');
    document.getElementById('appDialogErrorMsg')!.innerHTML = appDialogErrorMsg;
    document.getElementById('appDialogErrorMsg')!.style.display = 'block';
  }

  private onError(error: any): void {
    this.alertService.addAlert({ type: 'danger', translationKey: error.message });
  }
}
<div *ngIf="user">
  <h2>
    <span icTranslate="acmAdmin.user.home.editLabel">Edit User</span>
  </h2>

  <p style="display: none" id="appDialogErrorMsg" class="alert-dismissible alert-danger alert"></p>
  <ic-alert></ic-alert>
  <br />
  <div class="row">
    <div class="col-sm-12">
      <form name="editForm" role="form"  (ngSubmit)="save()" #editForm="ngForm">
        <div class="card">
          <div class="card-header">User Detail</div>
          <div class="card-block" style="padding: 1.5% 1.5% 1% 1.5%">
            <div class="rounded-pill row">
              <label class="col-sm-2 form-control-label" icTranslate="acmAdmin.user.name" for="field_firstName">Name</label>
              <div class="col-sm-4">
                <input type="text" class="form-control" name="firstName" id="field_firstName" [(ngModel)]="user.firstName" required />
                <div [hidden]="!(editForm.controls.firstName?.dirty && editForm.controls.firstName?.invalid)">
                  <small
                    class="form-text text-danger"
                    [hidden]="!editForm.controls.firstName?.errors?.required"
                    icTranslate="entity.validation.required"
                  >
                    This field is required.
                  </small>
                </div>
              </div>

              <label class="col-sm-2 form-control-label" icTranslate="acmAdmin.user.nric" for="field_nric">NRIC</label>
              <div class="col-sm-4">
                <input type="text" class="form-control" name="nric" id="field_nric" [(ngModel)]="user.nric" (keyup)="onKeyupNric()" required />
                <div *ngIf="invalidNric">
                  <small class="form-text text-danger">
                    Invalid NRIC. Please key in again.
                  </small>
                </div>
                <div [hidden]="!(editForm.controls.nric?.dirty && editForm.controls.nric?.invalid)">
                  <small
                    class="form-text text-danger"
                    [hidden]="!editForm.controls.nric?.errors?.required"
                    icTranslate="entity.validation.required"
                  >
                    This field is required.
                  </small>
                </div>
              </div>
            </div>

            <div class="rounded-pill row">
              <label class="col-sm-2 form-control-label" icTranslate="acmAdmin.user.salutation" for="field_salutation">Salutation</label>
              <div class="col-sm-4">
                <select id="field_salutation" class="form-control" name="salutation" [(ngModel)]="user.salutation">
                  <option [ngValue]="null"></option>
                  <option *ngFor="let salutation of salutations">{{ salutation }}</option>
                </select>
              </div>

              <label class="col-sm-2 form-control-label" icTranslate="acmAdmin.user.displayName" for="field_displayName">Display Name</label>
              <div class="col-sm-4">
                <input type="text" class="form-control" name="displayName" id="field_displayName" [(ngModel)]="user.displayName" required />
                <div [hidden]="!(editForm.controls.displayName?.dirty && editForm.controls.displayName?.invalid)">
                  <small
                    class="form-text text-danger"
                    [hidden]="!editForm.controls.displayName?.errors?.required"
                    icTranslate="entity.validation.required"
                  >
                    This field is required.
                  </small>
                </div>
              </div>
            </div>

            <div class="rounded-pill row">
              <label class="col-sm-2 form-control-label" icTranslate="acmAdmin.user.userInit" for="field_userInit">User Initial</label>
              <div class="col-sm-4">
                <input type="text" class="form-control" name="userInit" id="field_userInit" [(ngModel)]="user.userInit" maxlength="3" required />
                <div [hidden]="!(editForm.controls.userInit?.dirty && editForm.controls.userInit?.invalid)">
                  <small
                    class="form-text text-danger"
                    [hidden]="!editForm.controls.userInit?.errors?.required"
                    icTranslate="entity.validation.required"
                  >
                    This field is required.
                  </small>
                </div>
              </div>

              <label class="col-sm-2 form-control-label" icTranslate="acmAdmin.user.email" for="field_email">Email Address</label>
              <div class="col-sm-4">
                <input type="text" class="form-control" name="email" id="field_email" [(ngModel)]="user.email" required />
                <div [hidden]="!(editForm.controls.email?.dirty && editForm.controls.email?.invalid)">
                  <small
                    class="form-text text-danger"
                    [hidden]="!editForm.controls.email?.errors?.required"
                    icTranslate="entity.validation.required"
                  >
                    This field is required.
                  </small>
                </div>
              </div>
            </div>

            <div class="rounded-pill row">
              <label class="col-sm-2 form-control-label" icTranslate="acmAdmin.user.appt" for="field_appt">Appointment</label>
              <div class="col-sm-4">
                <input type="text" class="form-control" name="appt" id="field_appt" [(ngModel)]="user.appt" required />
                <div [hidden]="!(editForm.controls.appt?.dirty && editForm.controls.appt?.invalid)">
                  <small
                    class="form-text text-danger"
                    [hidden]="!editForm.controls.appt?.errors?.required"
                    icTranslate="entity.validation.required"
                  >
                    This field is required.
                  </small>
                </div>
              </div>

              <label class="col-sm-2 form-control-label" icTranslate="acmAdmin.user.unit" for="field_unit">Unit</label>
              <div class="col-sm-4">
                <input type="text" class="form-control" name="unit" id="field_unit" [(ngModel)]="user.unit" />
              </div>
            </div>

            <div class="rounded-pill row">
              <label class="col-sm-2 form-control-label" icTranslate="acmAdmin.user.status" for="field_status">Status</label>
              <div class="col-sm-4">
                <select id="field_status" class="form-control" name="status" [(ngModel)]="user.status" required>
                  <option [ngValue]="null"></option>
                  <option [ngValue]="status.codeId === user.status ? user.status : status.codeId" *ngFor="let status of statuses">
                    {{ status.codeDesc }}
                  </option>
                </select>
                <div [hidden]="!(editForm.controls.status?.dirty && editForm.controls.status?.invalid)">
                  <small
                    class="form-text text-danger"
                    [hidden]="!editForm.controls.status?.errors?.required"
                    icTranslate="entity.validation.required"
                  >
                    This field is required.
                  </small>
                </div>
              </div>

              <label class="col-sm-2 form-control-label" icTranslate="acmAdmin.user.effectiveDt" for="field_effectiveDt">Effective Date</label>
              <div class="col-sm-4">
                <div class="input-group">
                  <input id="field_effectiveDt" type="date" class="form-control" name="effectiveDt" [(ngModel)]="user.effectiveDt" />
                </div>
              </div>
            </div>

            <div class="rounded-pill row">
              <label class="col-sm-2 form-control-label" icTranslate="acmAdmin.user.expiryDt" for="field_expiryDt">Expiry Date</label>
              <div class="col-sm-4">
                <div class="input-group">
                  <input id="field_expiryDt" type="date" class="form-control" name="expiryDt" [(ngModel)]="user.expiryDt" />
                </div>
              </div>

              <label class="col-sm-2 form-control-label" icTranslate="acmAdmin.user.loginType" for="field_loginType">Login Type</label>
              <div class="col-sm-4">
                <select id="field_loginType" class="form-control" name="loginType" [(ngModel)]="userLogin.loginMechanism" required>
                  <option [ngValue]="null"></option>
                  <option
                    [ngValue]="loginType.codeId === userLogin.loginMechanism ? userLogin.loginMechanism : loginType.codeId"
                    *ngFor="let loginType of loginTypes"
                  >
                    {{ loginType.codeDesc }}
                  </option>
                </select>
                <div [hidden]="!(editForm.controls.loginType?.dirty && editForm.controls.loginType?.invalid)">
                  <small
                    class="form-text text-danger"
                    [hidden]="!editForm.controls.loginType?.errors?.required"
                    icTranslate="entity.validation.required"
                  >
                    This field is required.
                  </small>
                </div>
              </div>
            </div>

            <div class="rounded-pill row">
              <label class="col-sm-2 form-control-label" icTranslate="acmAdmin.user.loginId" for="field_loginId" *ngIf="showLoginId()">Login ID</label>
              <div class="col-sm-4" *ngIf="showLoginId()">
                <input
                  type="text"
                  class="form-control"
                  name="loginId"
                  id="field_loginId"
                  [(ngModel)]="userLogin.loginName"
                  required
                  pattern="^[^ ]+$"
                />
                <div [hidden]="!(editForm.controls.loginId?.dirty && editForm.controls.loginId?.invalid)">
                  <small
                    class="form-text text-danger"
                    [hidden]="!editForm.controls.loginId?.errors?.required"
                    icTranslate="entity.validation.required"
                  >
                    This field is required.
                  </small>
                  <small class="form-text text-danger" [hidden]="!editForm.controls.loginId?.errors?.pattern">
                    This field can not contain space.
                  </small>
                </div>
              </div>
            </div>

            <div class="rounded-pill row" style="margin-top: 1.5%">
              <div class="col-sm-12">
<!--                <input type="hidden" id="subjectId" name="subjectId" [(ngModel)]="user.subjectId" />-->

                <button id="saveEditButton" type="submit" [disabled]="editForm.form.invalid || isSaving" class="btn btn-primary btn-sm">
                  <fa-icon [icon]="'save'"></fa-icon>&nbsp;<span icTranslate="entity.action.save">Save</span>
                </button>
                <button type="button" [routerLink]="['../../../../admin/acm/ic-user']" class="btn btn-secondary btn-sm">
                  <fa-icon [icon]="'ban'"></fa-icon>&nbsp;<span icTranslate="entity.action.cancel">Cancel</span>
                </button>
                <button
                  type="button"
                  [disabled]="userLogin.loginMechanism !== 'PASSWORD'"
                  [routerLink]="['../..', 'ic-user-resetpwd', initialLoginName, 'reset', user.subjectId]"
                  [replaceUrl]="true"
                  class="btn btn-danger btn-sm"
                >
                  <fa-icon [icon]="'clock'"></fa-icon>&nbsp;<span>Reset Password</span>
                </button>
              </div>
            </div>
          </div>
        </div>
      </form>
    </div>

    <div style="margin-top: 20px" class="col-sm-12">
      <div class="card">
        <div class="card-header">Groups Assigned to User</div>
        <div class="card-block" style="padding: 1.5% 1.5% 1% 1.5%">
          <div class="table-responsive">
            <table class="table table-striped">
              <thead>
                <tr>
                  <th scope="col" style="width: 60px"><input type="checkbox" [(ngModel)]="selectedAllGroups" (click)="selectAllGroups($event)" /></th>
                  <th scope="col" style="width: 100px">S/No.</th>
                  <th scope="col">Group ID</th>
                  <th scope="col">Group Name</th>
                </tr>
              </thead>
              <tbody *ngIf="assignedGroupsLength() > 0">
                <tr *ngFor="let group of assignedGroups; let idx = index">
                  <td><input type="checkbox" [checked]="selectedAllGroups" (click)="selectOneGroup($event, group)" /></td>
                  <td>{{ idx + 1 }}</td>
                  <td>{{ group.groupId }}</td>
                  <td>{{ group.groupName }}</td>
                </tr>
              </tbody>
              <tbody *ngIf="assignedGroupsLength() == 0">
                <tr>
                  <td colspan="4">No Matching Records Found.</td>
                </tr>
              </tbody>
            </table>
          </div>

          <div style="margin-top: 20px">
            <button
              type="button"
              (click)="unAssignGroups()"
              *ngIf="assignedGroupsLength() > 0"
              [disabled]="!selectedGroups || selectedGroups.length === 0"
              class="btn btn-danger btn-sm"
            >
              <fa-icon [icon]="'times'"></fa-icon><span>&nbsp;Unassign Groups</span>
            </button>
            <button type="button" [routerLink]="['../assign', 'groups', user.subjectId]" [replaceUrl]="true" class="btn btn-primary btn-sm">
              <fa-icon [icon]="'check'"></fa-icon><span>&nbsp;Assign Groups</span>
            </button>
          </div>
        </div>
      </div>
    </div>

    <div style="margin-top: 20px" class="col-sm-12">
      <div class="card">
        <div class="card-header">Roles Assigned to User</div>
        <div class="card-block" style="padding: 1.5% 1.5% 1% 1.5%">
          <div class="table-responsive">
            <table class="table table-striped">
              <thead>
                <tr>
                  <th scope="col" style="width: 60px"><input type="checkbox" [(ngModel)]="selectedAllRoles" (click)="selectAllRoles($event)" /></th>
                  <th scope="col" style="width: 100px">S/No.</th>
                  <th scope="col">Role Name</th>
                  <th scope="col">Application</th>
                </tr>
              </thead>
              <tbody *ngIf="assignedRolesLength() > 0">
                <tr *ngFor="let role of assignedRoles; let idx = index">
                  <td><input type="checkbox" [checked]="selectedAllRoles" (click)="selectOneRole($event, role)" /></td>
                  <td>{{ idx + 1 }}</td>
                  <td>{{ role.resourceName }}</td>
                  <td>{{ role.application!.appName }}</td>
                </tr>
              </tbody>
              <tbody *ngIf="assignedRolesLength() == 0">
                <tr>
                  <td colspan="4">No Matching Records Found.</td>
                </tr>
              </tbody>
            </table>
          </div>

          <div style="margin-top: 20px">
            <button
              type="button"
              (click)="unAssignRoles()"
              *ngIf="assignedRolesLength() > 0"
              [disabled]="!selectedRoles || selectedRoles.length === 0"
              class="btn btn-danger btn-sm"
            >
              <fa-icon [icon]="'times'"></fa-icon><span>&nbsp;Unassign Roles</span>
            </button>
            <button type="button" [routerLink]="['../assign', 'roles', user.subjectId]" [replaceUrl]="true" class="btn btn-primary btn-sm">
              <fa-icon [icon]="'check'"></fa-icon><span>&nbsp;Assign Roles</span>
            </button>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
