import { HttpParams } from '@angular/common/http';
import { Component, EventEmitter, Input, OnInit, Output, SimpleChanges } from '@angular/core';
import { FormArray, FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Claim } from 'app/claim/model/claim.model';
import { ClaimService } from 'app/claim/service/claim.service';
import { EmpClaim } from '../../model/EmpClaim.model';
import { EmpClaimRequest } from '../../model/EmpClaimRequest.model';
import { ECClaimService } from '../../service/ec-claim.service';

@Component({
    selector: 'n2-company-section',
    templateUrl: './company-section.component.html',
    styleUrls: ['./company-section.component.scss']
  })
export class CompanySectionComponent implements OnInit {
    @Input() empClaimRequest: EmpClaimRequest;
    @Input() selected:boolean;
    @Input() disableInput: boolean;
    @Output() selectedChange = new EventEmitter<boolean>();

    // formGroup
    companyForm: FormGroup;
    addressForm: FormGroup;

    isDummyCoy: boolean;
    coyCsn: string;
    companyAddress: string;
    isHardcopyClaim: boolean;
    showInvalidAddressChkBox: boolean;
    isInvalidAddress = false;
    canEdit = false;
    showAddressModel = false;
    editedAddress = false;
    currentCODetail: any;

    empClaimDto: EmpClaim;
    claimDto: Claim;

    salutationOption = [
        {label: 'Mr', value: 'Mr'},
        {label: 'Ms', value: 'Ms'},
        {label: 'Mdm', value: 'Mdm'},
        {label: 'Dr', value: 'Dr'},
    ]

    constructor(
        private claimService: ClaimService,
        private formBuilder: FormBuilder
    ) { }

    ngOnInit(): void {
        console.log('Company Child component checked');
        this.empClaimDto = this.empClaimRequest?.empClaimDto;
        this.claimDto = this.empClaimRequest?.claimDto;
        this.isDummyCoy = this.checkDummyCoy(this.empClaimDto.csn);
        let fullAddress = '';
        this.initialiseFormGroup();
        if (this.disableInput === true) {
            this.companyForm.get('submittedBySalutation').disable();
            this.companyForm.get('submittedByName').disable();
            this.companyForm.get('submittedByAppt').disable();
            this.companyForm.get('submittedByEmail').disable();
            this.companyForm.get('submittedByContactNo').disable();
            this.companyForm.get('submittedByContactExt').disable();
        }
        if (this.isDummyCoy) {
            fullAddress += this.empClaimDto.blkNo ? 'Blk ' + String(this.empClaimDto.blkNo) + ' ' : '';
            fullAddress += this.empClaimDto.street ? String(this.empClaimDto.street) + ' ' : '';
            fullAddress += this.empClaimDto.unitNo ? '#' + String(this.empClaimDto.unitNo) + ' ' : '';
            fullAddress += this.empClaimDto.building ? String(this.empClaimDto.building) + ' ' : '';
            fullAddress += this.empClaimDto.postalCode ? 'Singapore ' + String(this.empClaimDto.postalCode) : '';
            this.companyAddress = fullAddress;
            this.canEdit = true;
            this.initialiseAddressFormGroup();
            if (this.claimDto.claimInitiateBy === 0) {
                this.isHardcopyClaim = true;
                this.patchFormValues();
            }
        } else {
            this.claimService.getCompanyInfo(this.empClaimDto.csn).subscribe((res: any) => {
                if (res.body.data) {
                    const coyRes = res.body.data;
                    fullAddress += coyRes.blk ? 'Blk ' + String(coyRes.blk) + ' ' : '';
                    fullAddress += coyRes.street ? String(coyRes.street) + ' ' : '';
                    fullAddress += coyRes.unit ? '#' + String(coyRes.unit) + ' ' : '';
                    fullAddress += coyRes.building ? String(coyRes.building) + ' ' : '';
                    fullAddress += coyRes.postalCode ? 'Singapore ' + String(coyRes.postalCode) : '';
                    this.companyAddress = fullAddress;
                    if (this.claimDto.claimInitiateBy === 0) {
                        this.isHardcopyClaim = true;
                        if (this.isHardcopyClaim && coyRes.eRegistered === 'Y') {
                            this.showInvalidAddressChkBox = true;
                        }
                        this.patchFormValues();
                    }
                }
            });
        }
        this.coyCsn = this.formatCoyCsn(this.empClaimDto.csn);

        this.currentCODetail = {
            certRole: this.empClaimDto?.certRole,
            certSalutation: this.empClaimDto?.certSalutation,
            certName: this.empClaimDto?.certName,
            certAppt: this.empClaimDto?.certAppt,
            certEmail: this.empClaimDto?.certEmail,
            certContactNo: this.empClaimDto?.certContactNo,
            certContactExt: this.empClaimDto?.certContactExt
        }
    }

    ngOnChanges(changes: SimpleChanges): void{
        console.log('ngOnChanges - Company Section', changes);
        if (changes['disableInput'].currentValue === false) {
            if (this.companyForm) {
                this.companyForm.get('submittedBySalutation').enable();
                this.companyForm.get('submittedByName').enable();
                this.companyForm.get('submittedByAppt').enable();
                this.companyForm.get('submittedByEmail').enable();
                this.companyForm.get('submittedByContactNo').enable();
                this.companyForm.get('submittedByContactExt').enable();
            }
        } else if (changes['disableInput'].currentValue === true) {
            if (this.companyForm) {
                this.companyForm.get('submittedBySalutation').disable();
                this.companyForm.get('submittedByName').disable();
                this.companyForm.get('submittedByAppt').disable();
                this.companyForm.get('submittedByEmail').disable();
                this.companyForm.get('submittedByContactNo').disable();
                this.companyForm.get('submittedByContactExt').disable();
            }
        }
        // if (changes['empClaimRequest'].currentValue) {
        //   this.initialiseFormGroup();
        // }
    }

    initialiseFormGroup() {
        return this.companyForm = this.formBuilder.group({
            companyName: [this.empClaimRequest.coyName],
            companySize: [this.empClaimRequest.coySize],
            submittedBy: [''],
            submittedBySalutation: [this.empClaimDto?.certSalutation],
            submittedByName: [this.empClaimDto?.certName],
            submittedByAppt: [this.empClaimDto?.certAppt],
            submittedByEmail: [this.empClaimDto?.certEmail, [Validators.email, Validators.maxLength(80)]],
            submittedByContactNo: [this.empClaimDto?.certContactNo, [Validators.minLength(8)]],
            submittedByContactExt: [this.empClaimDto?.certContactExt]
        });
    }

    patchFormValues():void{
        if (this.isHardcopyClaim === true) {
           this.companyForm.get('submittedByName') .setValidators(Validators.required);
           this.companyForm.get('submittedByAppt') .setValidators(Validators.required);
        }
        this.companyForm.updateValueAndValidity();
    }

    editedCertOfficer(): boolean {
        if (this.companyForm.get('submittedByName').value && this.currentCODetail.certName && this.companyForm.get('submittedByName').value !== this.currentCODetail.certName) {
            return true;
        } else if (this.companyForm.get('submittedByAppt').value && this.currentCODetail.certAppt && this.companyForm.get('submittedByAppt').value !== this.currentCODetail.certAppt) {
            return true;
        } else if (this.companyForm.get('submittedByEmail').value && this.currentCODetail.certEmail && this.companyForm.get('submittedByEmail').value !== this.currentCODetail.certEmail) {
            return true;
        } else if (this.companyForm.get('submittedByContactNo').value && this.currentCODetail.certContactNo && this.companyForm.get('submittedByContactNo').value !== this.currentCODetail.certContactNo) {
            return true;
        } else {
            return false;
        }
    }

    saveCompanySection() {
        this.empClaimRequest.coyName = this.companyForm.get('companyName').value;
        this.empClaimRequest.empClaimDto.certSalutation = this.companyForm.get('submittedBySalutation').value;
        this.empClaimRequest.empClaimDto.certName = this.companyForm.get('submittedByName').value;
        this.empClaimRequest.empClaimDto.certAppt = this.companyForm.get('submittedByAppt').value;
        this.empClaimRequest.empClaimDto.certEmail = this.companyForm.get('submittedByEmail').value;
        this.empClaimRequest.empClaimDto.certContactNo = this.companyForm.get('submittedByContactNo').value;
        this.empClaimRequest.empClaimDto.certContactExt = this.companyForm.get('submittedByContactExt').value;

        if (this.addressForm) {
            this.empClaimRequest.empClaimDto.blkNo = this.addressForm.get('blk').value;
            this.empClaimRequest.empClaimDto.street = this.addressForm.get('road').value;
            this.empClaimRequest.empClaimDto.unitNo = this.addressForm.get('unit').value;
            this.empClaimRequest.empClaimDto.building = this.addressForm.get('building').value;
            this.empClaimRequest.empClaimDto.postalCode = this.addressForm.get('postalCode').value;
        }
    }

    // ----------- ADDRESS FUNCTION ---------------

    initialiseAddressFormGroup(): FormGroup {
        return this.addressForm = this.formBuilder.group({
            blk: [''],
            road: [''],
            unit: [''],
            building: [''],
            postalCode: ['', [Validators.maxLength(6)]]
        });
    }

    displayAddressModel() {
        this.showAddressModel = true;
    }

    exitAddressModel() {
        this.showAddressModel = false;
    }

    updateAddress() {
        let fullAddress = '';
        fullAddress += this.addressForm.get('blk').value ? 'Blk ' + String(this.addressForm.get('blk').value) + ' ' : '';
        fullAddress += this.addressForm.get('road').value ? String(this.addressForm.get('road').value) + ' ' : '';
        fullAddress += this.addressForm.get('unit').value ? '#' + String(this.addressForm.get('unit').value) + ' ' : '';
        fullAddress += this.addressForm.get('building').value ? String(this.addressForm.get('building').value) + ' ' : '';
        fullAddress += this.addressForm.get('postalCode').value ? 'Singapore ' + String(this.addressForm.get('postalCode').value) : '';

        if (fullAddress.length > 1) {
            this.companyAddress = fullAddress;
        }

        this.editedAddress = true;
        this.showAddressModel = false;
    }
    // ------------------------------------------

    checkDummyCoy(csn: string): boolean {
        const strLength = csn.length;
        const dummyCsn = csn.substring(strLength-5);
        return dummyCsn === String('99999');
    }

    formatCoyCsn(csn: string): string {
        const strLength = csn.length;
        const csn1 = csn.substring(0, strLength-5);
        const csn2 = csn.substring(strLength-5, strLength-2);
        const csn3 = csn.substring(strLength-2, strLength);
        return csn1 + '-'+ csn2 + '-' + csn3;
    }

    accordionClick(): void{
        this.selectedChange.emit(this.selected);
    }

    showErrorMsg(controlName: string, errorName: string): boolean {
        return this.companyForm.get(controlName)?.hasError(errorName) && this.companyForm.get(controlName)?.touched;
    }

    consoleLog(){
        console.log('companyFormGroup', this.companyForm);
        console.log('disableInput', this.disableInput);
    }
}
