import { Component, OnInit, OnDestroy } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { HttpResponse } from '@angular/common/http';
import { Subscription, combineLatest } from 'rxjs';
import { AlertService } from 'app/core/util/alert.service';
import { EventManager } from 'app/core/util/event-manager.service';
import { ParseLinks } from 'app/core/util/parse-links.service';
import { Application } from './application.model';
import { ApplicationService } from './application.service';
import { ITEMS_PER_PAGE } from 'app/config/pagination.constants';
import { AccountService } from 'app/core/auth/account.service';

@Component({
  selector: 'ic-application',
  templateUrl: './application.component.html',
})
export class ApplicationComponent implements OnInit, OnDestroy {
  currentAccount: any;
  applications: Application[] = [];
  error: any;
  success: any;
  eventSubscriber?: Subscription;
  routeData: any;
  links: any;
  totalItems: any;
  queryCount: any;
  itemsPerPage = ITEMS_PER_PAGE;
  page!: number;
  ascending!: boolean;
  predicate!: string;
  previousPage: any;
  reverse: any;
  searchCriteria: any;

  constructor(
    private applicationService: ApplicationService,
    private parseLinks: ParseLinks,
    private alertService: AlertService,
    private accountService: AccountService,
    private activatedRoute: ActivatedRoute,
    private router: Router,
    private eventManager: EventManager
  ) {
    this.searchCriteria = {
      appCode: '',
      appName: '',
      appDesc: '',
    };
  }

  loadAll(): void {
    this.applicationService
      .query({
        appCode: this.searchCriteria.appCode,
        appName: this.searchCriteria.appName,
        appDesc: this.searchCriteria.appDesc,
        page: this.page - 1,
        size: this.itemsPerPage,
        sort: this.sort(),
      })
      .subscribe(
        (res: HttpResponse<any>) => this.onSuccess(res.body, res.headers),
        (res: HttpResponse<any>) => this.onError(res.body)
      );
  }

  loadPage(page: number): void {
    if (page !== this.previousPage) {
      this.previousPage = page;
      this.transition();
    }
  }

  transition(): void {
    this.router.navigate(['/admin/acm/ic-application'], {
      queryParams: {
        page: this.page,
        size: this.itemsPerPage,
        sort: this.predicate + ',' + (this.reverse ? 'asc' : 'desc'),
      },
    });
  }

  search(): void {
    this.links = {
      last: 0,
    };
    this.page = 0;
    this.predicate = 'appCode';
    this.reverse = false;
    this.loadAll();
  }

  clear(): void {
    this.searchCriteria = {
      appCode: '',
      appName: '',
      appDesc: '',
    };
    this.page = 0;
    this.router.navigate([
      '/admin/acm/ic-application',
      {
        page: this.page,
        sort: this.predicate + ',' + (this.reverse ? 'asc' : 'desc'),
      },
    ]);
  }

  ngOnInit(): void {
    this.accountService.identity().subscribe(account => (this.currentAccount = account));
    this.handleNavigation();
    this.registerChangeInApplications();
  }

  ngOnDestroy(): void {
    if (this.eventSubscriber) {
      this.eventManager.destroy(this.eventSubscriber);
    }
  }

  trackId(index: number, item: Application): any {
    return item.id;
  }

  registerChangeInApplications(): void {
    this.eventSubscriber = this.eventManager.subscribe('applicationListModification', () => this.loadAll());
  }

  sort(): any {
    const result = [this.predicate + ',' + (this.reverse ? 'asc' : 'desc')];
    if (this.predicate !== 'appCode') {
      result.push('appCode');
    }
    return result;
  }

  private handleNavigation(): void {
    combineLatest([this.activatedRoute.data, this.activatedRoute.queryParamMap]).subscribe(([data, params]) => {
      const page = params.get('page');
      this.page = page !== null ? +page : 1;
      const sort = (params.get('sort') ?? data['defaultSort']).split(',');
      this.predicate = sort[0];
      this.ascending = sort[1] === 'asc';
      this.loadAll();
    });
  }

  private onSuccess(data: any, headers: any): void {
    this.links = this.parseLinks.parse(headers.get('link'));
    this.totalItems = headers.get('X-Total-Count');
    this.queryCount = this.totalItems;
    this.applications = data;
  }

  private onError(error: any): void {
    this.alertService.addAlert({ type: 'danger', translationKey: error.message });
  }
}
<div>
  <h2>
    <span icTranslate="acmAdmin.application.home.title">Applications</span>
  </h2>
  <ic-alert></ic-alert>
  <br />
  <div class="row">
    <form name="searchForm" class="col-sm-12">
      <div class="card">
        <div class="card-header"><span icTranslate="acmAdmin.home.SearchCriteria">Search Criteria</span></div>
        <div class="card-block" style="padding: 1.5%">
          <div class="rounded-pill row">
            <label class="col-sm-2 form-control-label">Application Code</label>
            <div class="col-sm-4">
              <input type="text" class="form-control" name="appCode" id="appCode" [(ngModel)]="searchCriteria.appCode" />
            </div>
            <label class="col-sm-2 form-control-label">Application Name</label>
            <div class="col-sm-4">
              <input type="text" class="form-control" name="appName" id="appName" [(ngModel)]="searchCriteria.appName" />
            </div>
          </div>

          <div class="rounded-pill row">
            <label class="col-sm-2 form-control-label">Application Description</label>
            <div class="col-sm-4">
              <input type="text" class="form-control" name="appDesc" id="appDesc" [(ngModel)]="searchCriteria.appDesc" />
            </div>
          </div>

          <div style="margin-top: 1.5%">
            <button id="searchApp" type="button" class="btn btn-warning btn-sm" (click)="search()">
              <fa-icon [icon]="'search'"></fa-icon>
              <span class="fa fa-search" icTranslate="acmAdmin.home.search"></span>
            </button>
            <button type="button" class="btn btn-secondary btn-sm" (click)="clear()">
              <fa-icon [icon]="'trash-alt'"></fa-icon>
              <span class="fa fa-trash-o" icTranslate="acmAdmin.home.reset"></span>
            </button>
            <button id="createNewApplication" class="btn btn-primary btn-sm" [routerLink]="['../ic-application-new']">
              <fa-icon [icon]="'plus'"></fa-icon>
              <span icTranslate="acmAdmin.application.home.createLabel">Create new Application</span>
            </button>
          </div>
        </div>
      </div>
    </form>
  </div>
  <br />
  <div class="card">
    <div class="card-header"><span icTranslate="acmAdmin.home.SearchResult">Search Criteria</span></div>
    <div class="card-block" style="padding: 1.5% 1.5% 0 1.5%">
      <div class="table-responsive" *ngIf="applications">
        <table class="table table-striped">
          <thead>
            <tr icSort [(predicate)]="predicate" [(ascending)]="reverse" [callback]="transition.bind(this)">
              <th scope="col"><span icTranslate="acmAdmin.home.sn">S/No.</span></th>
              <th scope="col" icSortBy="appCode">
                <span icTranslate="acmAdmin.application.appCode">App Code</span> <fa-icon [icon]="'sort'"></fa-icon>
              </th>
              <th scope="col" icSortBy="appName">
                <span icTranslate="acmAdmin.application.appName">App Name</span> <fa-icon [icon]="'sort'"></fa-icon>
              </th>
              <th scope="col" icSortBy="appDesc">
                <span icTranslate="acmAdmin.application.appDesc">App Desc</span> <fa-icon [icon]="'sort'"></fa-icon>
              </th>
              <th scope="col" icSortBy="updatedBy">
                <span icTranslate="acmAdmin.application.updatedBy">Updated By</span> <fa-icon [icon]="'sort'"></fa-icon>
              </th>
              <th scope="col" icSortBy="updatedDt">
                <span icTranslate="acmAdmin.application.updatedDt">Updated Date</span> <fa-icon [icon]="'sort'"></fa-icon>
              </th>
              <th scope="col" style="text-align: center">
                <span icTranslate="acmAdmin.home.operation">Operation</span>
              </th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let application of applications; let idx = index; trackBy: trackId">
              <td>{{ idx + 1 }}</td>
              <td>{{ application.appCode }}</td>
              <td>{{ application.appName }}</td>
              <td>{{ application.appDesc }}</td>
              <td>{{ application.updatedBy }}</td>
              <td>{{ application.updatedDt | date: 'yyyy-MM-dd' }}</td>
              <td class="text-center">
                <div class="btn-group flex-btn-group-container">
                  <button
                    [id]="application.appCode"
                    type="submit"
                    [routerLink]="['../ic-application/' + application.appId]"
                    [replaceUrl]="true"
                    class="btn btn-primary btn-sm"
                  >
                    <fa-icon [icon]="'pencil-alt'"></fa-icon>
                    <span class="hidden-md-down" icTranslate="entity.action.edit">Edit</span>
                  </button>
                </div>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
      <div *ngIf="applications && applications.length">
        <div class="row justify-content-center">
          <ic-item-count [params]="{ page: page, totalItems: totalItems, itemsPerPage: itemsPerPage }"></ic-item-count>
        </div>
        <div class="row justify-content-center">
          <ngb-pagination
            [collectionSize]="totalItems"
            [pageSize]="itemsPerPage"
            [(page)]="page"
            (pageChange)="loadPage(page)"
          ></ngb-pagination>
        </div>
      </div>
    </div>
  </div>
</div>
