import { Component, OnInit, OnDestroy } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { NgbActiveModal } from '@ng-bootstrap/ng-bootstrap';
import { EventManager } from 'app/core/util/event-manager.service';
import { Resource } from '../resource/resource.model';
import { RoleService } from './role.service';
import { RolePopupService } from './role-popup.service';

@Component({
  selector: 'ic-role-delete-dialog',
  templateUrl: './role-delete-dialog.component.html',
})
export class RoleDeleteDialogComponent {
  role: Resource = new Resource();

  constructor(
    private roleService: RoleService,
    public activeModal: NgbActiveModal,
    private eventManager: EventManager,
    private router: Router
  ) {}

  clear(): void {
    this.activeModal.dismiss('cancel');
    this.backToPrevious();
  }

  confirmDelete(id: string): void {
    this.roleService.delete(id).subscribe(() => {
      this.eventManager.broadcast({
        name: 'roleListModification',
        content: 'Deleted an role',
      });
      this.activeModal.dismiss(true);
      this.backToPrevious();
    });
  }

  backToPrevious(): void {
    setTimeout(() => {
      this.router.navigate(['admin/acm/ic-role']);
    }, 0);
  }
}

@Component({
  selector: 'ic-role-delete-popup',
  template: '',
})
export class RoleDeletePopupComponent implements OnInit, OnDestroy {
  routeSub: any;

  constructor(private route: ActivatedRoute, private rolePopupService: RolePopupService) {}

  ngOnInit(): void {
    this.routeSub = this.route.params.subscribe(params => {
      this.rolePopupService.open(RoleDeleteDialogComponent as Component, params['id']);
    });
  }

  ngOnDestroy(): void {
    this.routeSub.unsubscribe();
  }
}
<form name="deleteForm" (ngSubmit)="confirmDelete(role.resourceId)">
  <div class="modal-header">
    <h4 class="modal-title" icTranslate="entity.delete.title">Confirm delete operation</h4>
    <button type="button" class="close" data-bs-dismiss="modal" aria-hidden="true" (click)="clear()">&times;</button>
  </div>
  <div class="modal-body">
    <ic-alert-error></ic-alert-error>
    <p icTranslate="acmAdmin.role.delete.question" [translateValues]="{ id: role.resourceName }">
      Are you sure you want to delete this Role?
    </p>
  </div>
  <div class="modal-footer">
    <button id="deleteRole" type="submit" class="btn btn-danger btn-sm">
      <fa-icon [icon]="'times'"></fa-icon>&nbsp;<span icTranslate="entity.action.delete">Delete</span>
    </button>
    <button type="button" class="btn btn-secondary btn-sm" data-bs-dismiss="modal" (click)="clear()">
      <fa-icon [icon]="'ban'"></fa-icon>&nbsp;<span icTranslate="entity.action.cancel">Cancel</span>
    </button>
  </div>
</form>
