import { HttpParams } from '@angular/common/http';
import { AfterViewChecked, AfterViewInit, Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { AbstractControl, FormArray, FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { SEClaimService } from 'app/claim/se-claim/service/se-claim.service';
import { ClaimService } from 'app/claim/service/claim.service';
import { ViewRemarksComponent } from 'app/claim/pop-up/view-remarks/view-remarks.component';
import { DateUtilsService } from 'app/core/util/date-utils.service';
import { CustomToastService } from 'app/layouts/service/customtoast.service';
import { NspConfirmationService } from 'app/shared/confirm/nsp-confirm.service';
import { DateValidator } from 'app/shared/validator/date-validator.directive';
import { TimeValidator } from 'app/shared/validator/time.directive';
import moment from 'moment';
import { DialogService } from 'primeng/dynamicdialog';
import { FileUploadComponent } from 'app/shared/component/file-upload/file-upload.component';
import { FileUploadValidator2 } from 'app/shared/validator/fileupload2.directive';
import { LazyLoadEvent, Message } from 'primeng/api';
import { Ns2HttpService } from 'app/shared/service/ns2-http.service';
import { Table } from 'primeng/table';
import {ClaimSuppDoc} from "app/claim/model/claim.model";
import { AccountService } from 'app/core/auth/account.service';
import claim from '../../../../i18n/en/claim.json';
import { PublicHolidayService } from 'app/administration/black-out-date/public-holiday/service/public-holiday.service';
import { faL } from '@fortawesome/free-solid-svg-icons';
import { CommonValidator } from 'app/shared/validator/common-validator.directive';
import { ClaimSuppDocComponent } from 'app/claim/pop-up/view-supporting-doc/claim-supp-doc/claim-supp-doc.component';


@Component({
  selector: 'n2-edit-hardcopy-se',
  templateUrl: './edit-hardcopy-se.component.html',
  styleUrls: ['./edit-hardcopy-se.component.scss'],
  styles: [`
  :host ::ng-deep .p-datatable .p-datatable-thead > tr > th {
      position: -webkit-sticky;
      position: sticky;
      top: 0rem;
  }

  .layout-news-active :host ::ng-deep .p-datatable tr > th {
      top: 0rem;
  }
`]
})
export class EditHardcopySeComponent implements OnInit, AfterViewInit  {

  nsInfo3 = "";
  nsInfo4 = "";
  routeOutMsg = "";
  hasChanged = false;

  txnIdList: string[] = [];
  claimJson: any;
  seHardCopyDetails: any = {};
  newClaimDto: any = {};
  public selectedRows: any[] = [];
  selectAll = false;
  loading!: boolean;
  suppDocList: any[] = [];
  indexTimes = 0;
  currentAccount: any;
  lastTableLazyLoadEvent: LazyLoadEvent;
  pageData: { currentPage: number } = {
    currentPage: 0
  }
  pageSize = 10;
  dateFormat = "DD-MM-YYYY";
  displaySuppDocDialog: any = {display: false};
  displayRemarks: any = {display: false};
  displaySuccessResp: any = {display: false};
  readonly ALLOWABLE_EXTENSION: string = ".jpg,.png,.gif,.pdf,.csv";
  readonly DRAFT_CLAIM_STATUS = "PC1";
  overlappingClaims: any[] = [];
  params: any;

  outsideCurYr = false;

  nsmanDraftCount = 0;
  caseId = "";
  claimId = "";
  occupationList: any[] = [];
  radioCategories: any[] = [{name: 'Income Tax', key: '2'}, {name: 'Average Income', key: '1'}, {name: 'Replacement', key: '3'}];
  itemStatusList: any[] =
  [{name: 'Pend Capture', key: 'PCA'}, {name: 'Closed (Reject)', key: 'COR'}, {name: 'Closed (Withdrawn by NSMan)', key: 'CNS'},
  {name:'Closed (Withdrawn by Employer)', key:'CCO'}];

  hardcopySEForm: FormGroup = this.formBuilder.group({
    claimOption: new FormControl(this.radioCategories[0].key,Validators.required),
    trgStartDte: new FormControl(null, Validators.required),
    trgEndDte: new FormControl(null, Validators.required),
    workStartTime: new FormControl('0001', null),
    workEndTime: new FormControl('2359', null),
    occupation: new FormControl('', Validators.required),
    otherOccupDesc: new FormControl('', null),
    itemStatus:  new FormControl(this.itemStatusList[0].key, Validators.required),
    company: [null],
    commencementDte: new FormControl(null, Validators.required),
  }, {validators: [
      DateValidator.dateLessThan('trgStartDte', 'trgEndDte'),
      DateValidator.dateRangeMoreThan('trgStartDte', 'trgEndDte', 60),
      CommonValidator.mustNotEqualValidator('workStartTime', 'workEndTime')
    ]
  });
  suppDocFileForm: FormGroup = this.formBuilder.group({
    supportingDocuments: [[], [FileUploadValidator2.allowableExtension(this.ALLOWABLE_EXTENSION)]],
  });

  remarksForm: FormGroup = this.formBuilder.group({
    closedRejectedRemarks: new FormControl('', [Validators.required]),
  });

  // @ViewChild('claimSuppDocSaved') claimSuppDocSaved: ElementRef;
  @ViewChild('claimSuppDocSaved', {static: false})
  public claimSuppDocSaved: ClaimSuppDocComponent;

  @ViewChild('suppDocFileUpload', {static: false})
  private seSupportingDocuments: FileUploadComponent;

  @ViewChild('supportDocTable', {static: true})
  private table!: Table;






  constructor(
    private formBuilder: FormBuilder,
    public dateUtilsService: DateUtilsService,
    private seClaimService: SEClaimService,
    private toastService: CustomToastService,
    private claimService: ClaimService,
    private route: ActivatedRoute,
    private dialogService: DialogService,
    private nspConfirmService: NspConfirmationService,
    private router: Router,
    private ns2Http: Ns2HttpService,
    private account: AccountService,
    private publicHolidayService: PublicHolidayService,


  ) {
    console.log("x")
    this.claimJson = claim;
    // this.claimSuppDocSaved.
   }
  ngAfterViewInit(): void {
    console.log("x")
  }

  ngOnInit(): void {
    this.account.identity().subscribe((account: any) => {
      this.currentAccount = this.copyAccount(account);
    });
    this.retrieveOccupationList();
    this.outsideCurYr =  this.fallOutsideCurrentYr(this.hardcopySEForm?.get('trgStartDte')?.value)||
    this.fallOutsideCurrentYr(this.hardcopySEForm?.get('trgEndDte')?.value);
    this.retrieveSEHardCopyClaimDetails();

  }
  // ngAfterViewChecked() {
  //   // console.log("After view got Checked");
  //   console.log(this.claimSuppDocSaved);
  // }
  
  retrieveNsmanDraftCount() {
    let params = new HttpParams();
    params = params.append('nric',this.seHardCopyDetails?.headerDto?.nsManNric);
    params = params.append('claimStatus',this.DRAFT_CLAIM_STATUS);

    this.claimService.retrieveClaimCntByClaimStatusNric({params}).subscribe(resp => {
      if(resp.ok && resp.body.data){
        this.nsmanDraftCount = resp.body.data;
      }},
      (error: any) => {
        console.log("error is" + String(error?.error?.error_desc));
        this.toastService.error(String(error?.error?.error_desc));
      }
    );
  }

  setConditionalValidationForOtherOccupDesc(): void{
    if(this.isNotEmpty(this.hardcopySEForm.get('occupation').value)
      && this.hardcopySEForm.get('occupation')?.value==='990'){
        this.hardcopySEForm.get('otherOccupDesc')?.setValidators([Validators.required]);
        this.hardcopySEForm.get('otherOccupDesc').markAsTouched();

    } else {
      this.hardcopySEForm.controls['otherOccupDesc'].setValidators(null);
      this.hardcopySEForm.controls['otherOccupDesc'].setErrors(null);
    }
  }

  getDay(date: Date): string{
    return moment(date).format('ddd');
  }

  copyAccount(account: any): any {
    console.log("the real account", account)
    return {
      email: account.email,
      firstName: account.firstName,
      lastName: account.lastName,
      login: account.username,
      authorities: account.authorities,
      groups: account.groups,
      subjectId: account.subjectId,
      userInitials: account.userInitials
    };
  }

  patchNewFormValues(): void{
    this.hardcopySEForm.get('claimOption').setValue(this.newClaimDto?.claimOption);
    this.hardcopySEForm.get('trgStartDte').setValue(this.dateUtilsService.convertStrToDate(this.newClaimDto?.trgStartDte, this.dateFormat));
    this.hardcopySEForm.get('trgEndDte').setValue(this.dateUtilsService.convertStrToDate(this.newClaimDto?.trgEndDte, this.dateFormat));
    this.hardcopySEForm.get('company').setValue(this.newClaimDto?.company);
    this.hardcopySEForm.get('occupation').setValue(this.seHardCopyDetails?.occupationCode);
    this.hardcopySEForm.get('otherOccupDesc').setValue(this.seHardCopyDetails?.otherOccupationDesc);
    this.hardcopySEForm.get('commencementDte').setValue(this.seHardCopyDetails?.commencementDte? this.dateUtilsService.convertStrToDate(this.seHardCopyDetails?.commencementDte, this.dateFormat): null);
    this.hardcopySEForm.updateValueAndValidity();

  }

  tester(): void{
    console.log("the errors", this.hardcopySEForm)
    console.log("the supp doc lkength", this.suppDocFileForm.controls['supportingDocuments'].value.length)
    console.log("the overlapping",this.hardcopySEForm.errors)
  }

  patchFormValues():void{

    this.hardcopySEForm.get('claimOption').setValue(this.seHardCopyDetails?.headerDto?.claimOption);
    if(this.seHardCopyDetails.trgStartDte){
      this.hardcopySEForm.get('trgStartDte').setValue(this.dateUtilsService.convertStrToDate(this.seHardCopyDetails?.trgStartDte, this.dateFormat));
    }
    if(this.seHardCopyDetails.trgEndDte){
      this.hardcopySEForm.get('trgEndDte').setValue(this.dateUtilsService.convertStrToDate(this.seHardCopyDetails?.trgEndDte, this.dateFormat));
    }
    if(this.seHardCopyDetails.commencementDte){
      this.hardcopySEForm.get('trgEndDte').setValue(this.dateUtilsService.convertStrToDate(this.seHardCopyDetails?.trgEndDte, this.dateFormat));
    }
    this.hardcopySEForm.get('occupation').setValue(this.seHardCopyDetails?.occupationCode);
    this.hardcopySEForm.get('otherOccupDesc').setValue(this.seHardCopyDetails?.otherOccupationDesc);
    this.hardcopySEForm.get('company').setValue(this.seHardCopyDetails?.coyName);
    this.hardcopySEForm.get('workStartTime').setValue(this.seHardCopyDetails?.workStartTime);
    this.hardcopySEForm.get('workEndTime').setValue(this.seHardCopyDetails?.workEndTime);
    this.hardcopySEForm.get('commencementDte').setValue(this.dateUtilsService.convertStrToDate(this.seHardCopyDetails?.commencementDte, this.dateFormat));
    this.hardcopySEForm.updateValueAndValidity();

  }

  retrieveSEHardCopyClaimDetails(){
    if(this.route.snapshot.paramMap.get("claimId")){
      this.claimId = this.route.snapshot.paramMap.get("claimId");
      this.seClaimService.retrieveSEClaimDetails(this.claimId, null).subscribe(resp => {
        if (resp.body.data) {
          this.seHardCopyDetails = resp.body.data;
          this.retrieveNsmanDraftCount();
          console.log("the hard copy details", this.seHardCopyDetails)
          this.patchFormValues();
          this.caseId = this.seHardCopyDetails.headerDto.caseId;
          this.params = {
            nric: this.seHardCopyDetails?.headerDto?.nsManNric,
            name: this.seHardCopyDetails?.headerDto?.nsManName,
            caseId: this.seHardCopyDetails?.headerDto?.caseId,
            claimId: this.seHardCopyDetails?.claimId
          }

        }
        this.valueChangeSubscribe();

      });
    }else if(this.route.snapshot.paramMap.get("newClaimDto"))
    {
      this.newClaimDto = JSON.parse(this.route.snapshot.paramMap.get("newClaimDto"))
      console.log("retrieving new se claim details", this.newClaimDto);
      let params = new HttpParams();
      params = params.append('nric',this.newClaimDto?.nric);
      params = params.append('trgStartDte',this.newClaimDto?.trgStartDte);

      this.seClaimService.retrieveNewSEHardCopyClaimDetails({params}).subscribe(resp => {
        if (resp.body.data) {
          this.seHardCopyDetails = resp.body.data;
          this.retrieveNsmanDraftCount();
          console.log("the new hard copy details", this.seHardCopyDetails)
          this.patchNewFormValues();
          this.valueChangeSubscribe();

        }});
    }
  }

  valueChangeSubscribe():void{
    const initialValue = this.hardcopySEForm.value;
    this.setConditionalValidationForWorkHour();
    this.hardcopySEForm.valueChanges.subscribe((change:any)=>{
      this.hasChanged = Object.keys(initialValue).some(key => this.hardcopySEForm.value[key] !==
        initialValue[key]);
      if(this.seHardCopyDetails?.headerDto?.nsManNric &&
      this.hardcopySEForm.get('claimOption').value && this.hardcopySEForm.get('trgStartDte').value && this.hardcopySEForm.get('trgEndDte').value){
        //call overlapping
        let params = new HttpParams();
        params = params.append('claimOption',this.hardcopySEForm.get('claimOption').value);
        params = params.append('nric',this.seHardCopyDetails?.headerDto?.nsManNric);
        params = params.append('claimStartDate',this.dateUtilsService.convertDateToStr(this.hardcopySEForm.get('trgStartDte').value, this.dateFormat));
        params = params.append('claimEndDate',this.dateUtilsService.convertDateToStr(this.hardcopySEForm.get('trgEndDte').value, this.dateFormat));
        if(this.hardcopySEForm.get('claimOption').value !== '2'){
          params = params.append('coyName', this.hardcopySEForm.get('company').value);
        }
        this.seClaimService.retrieveOverlappingSEClaim({params}).subscribe(resp => {
          if (resp.ok){
            let errMsg = "";
            this.overlappingClaims = resp.body.data;
            if(resp.body.data.length>0 ){
              (resp.body.data as Array<any>).forEach(x=>{
                errMsg = errMsg.concat(x.caseId).concat("(").concat("Draft by ").concat(x.draftByInit).concat(")").concat(",");
              })
              errMsg = errMsg.concat("proceed to upload supporting document CLM-E002");
              let templateErrMsg = claim.nspay2App.claim.error['CLM-E002'];
              templateErrMsg = templateErrMsg.replace("{{overlappingCaseIdList}}",errMsg );
              if(!this.hardcopySEForm.hasError('overlapping')){
                this.hardcopySEForm.setErrors({...this.hardcopySEForm.errors || {}, 'overlapping': templateErrMsg});
              }
            }else{
              if (this.hardcopySEForm?.errors && this.hardcopySEForm?.errors['overlapping']) {
                delete this.hardcopySEForm.errors['overlapping'];
              }
            }
          }
          },
          (error: any) => {
            console.log("error is" + String(error?.error?.error_desc));
            this.toastService.error(String(error?.error?.error_desc));
          }
        );
      }
      this.setConditionalValidationForOtherOccupDesc();
      this.onBlurTrgStartEndDt();
    })
    this.hardcopySEForm.get('claimOption').valueChanges.subscribe(
      (change: any) => {
        console.log("claim is", change)
        if(change !== '2'){
          this.hardcopySEForm.get('company')?.setValidators([Validators.required]);
        } else {
          console.log("reseting")
          this.hardcopySEForm.controls['company'].removeValidators(Validators.required);
          this.hardcopySEForm.get('company').reset();
        }
        this.hardcopySEForm.get('company')?.updateValueAndValidity();
      }
    );
    this.hardcopySEForm.get('trgEndDte').valueChanges.subscribe(
      () => {
        const year = (this.hardcopySEForm.get('trgEndDte').value as Date).getFullYear();
        this.validateHasHoliday(year, (this.hardcopySEForm.get('trgEndDte')));
        this.setConditionalValidationForWorkHour();

      }
    );
    this.hardcopySEForm.get('trgStartDte').valueChanges.subscribe(
      () => {
        const year = (this.hardcopySEForm.get('trgStartDte').value as Date).getFullYear();
        this.validateHasHoliday(year, (this.hardcopySEForm.get('trgStartDte')));
        this.setConditionalValidationForWorkHour();
      }
    );
  }

  isTrgSameDay(): boolean{
      return moment(this.hardcopySEForm?.get('trgStartDte').value).format('YYYY-MM-DD') === moment(this.hardcopySEForm?.get('trgEndDte').value).format('YYYY-MM-DD') ? true : false;

  }

 

  onBlurTrgStartEndDt(): void{
    this.outsideCurYr =  this.fallOutsideCurrentYr(this.hardcopySEForm?.get('trgStartDte')?.value)||
    this.fallOutsideCurrentYr(this.hardcopySEForm?.get('trgEndDte')?.value);
  }

  fallOutsideCurrentYr(date: any): boolean{
    const curYr = new Date().getFullYear();
    const inputDate = new Date(date);
    if(inputDate.getFullYear()< curYr || inputDate.getFullYear()> curYr){
      return true;
    }else return false;
  }

  isNotEmpty(obj: any): boolean{
    return obj!==undefined && obj!==null && obj!=="";
  }

  setConditionalValidationForWorkHour(): void{
    if (this.isTrgSameDay()) {    
      console.log('IS TRG SAME DAY');
      this.hardcopySEForm.get('workStartTime').setValidators([Validators.required]);
      this.hardcopySEForm.get('workStartTime').updateValueAndValidity();
      this.hardcopySEForm.get('workEndTime').setValidators([Validators.required]);
      this.hardcopySEForm.get('workEndTime').updateValueAndValidity();

    }else{
      this.hardcopySEForm.get('workStartTime').clearValidators();
      this.hardcopySEForm.get('workStartTime').setValue(null);
      this.hardcopySEForm.get('workStartTime').updateValueAndValidity();
      this.hardcopySEForm.get('workEndTime').clearValidators();
      this.hardcopySEForm.get('workEndTime').setValue(null);
      this.hardcopySEForm.get('workEndTime').updateValueAndValidity();

    }
  }

  testError(): void{
    console.log("the erorrs are", this.hardcopySEForm.errors)
  }

  padLeft(text: string, padChar: string, size: number): string {
    return (String(padChar).repeat(size) + text).substr(size * -1, size);
  }

  onBeforeInput(event: InputEvent): boolean{
    if(event.inputType === "insertText" || event.inputType === "insertFromPaste") {
      return /^\d+$/.test(event.data);
    }
    return true;
  }

  onBlur(event: any, controlName: string): void{
    console.log(event.target.value);
    if(event.target.value) {
      this.hardcopySEForm.get(controlName).setValue(this.padLeft(event.target.value, "0", 4));
    }
  }

  isLongPeriod():boolean{
    if(this.hardcopySEForm.get('trgStartDte').value && this.hardcopySEForm.get('trgEndDte').value){
      const diffDays = this.dateUtilsService.getDaysBetween((this.hardcopySEForm.get('trgStartDte').value as Date),(this.hardcopySEForm.get('trgEndDte').value as Date));
      return diffDays>30 && diffDays<60;
    }else return false;
  }

  
  validateHasHoliday(year: number, ctrl: AbstractControl): void{
    let params = new HttpParams();
    params = params.append('year',year);
    this.publicHolidayService.getHolidayByYear({params}).subscribe(resp => {
      console.log("holiday is", resp)
      if (!resp.ok ||  resp.body.length<=0){
        ctrl.setErrors({'noHoliday': true});
      }

      },
      (error: any) => {
        console.log("error is" + String(error?.error?.error_desc));
        this.toastService.error(String(error?.error?.error_desc));
      }
    );
  }

  isLateClaim(): boolean{
    if(this.hardcopySEForm.get('trgStartDte').value){
      const claimStart = this.hardcopySEForm.get('trgStartDte')?.value;
      const dateNow  = new Date();
      const threshold = new Date(dateNow.getFullYear()-1, dateNow.getMonth(),dateNow.getDay()); 
      return claimStart<threshold;
    }else return false;
  }

  workHourStartMoreThanEnd() {
    if (this.hardcopySEForm.get('workStartTime').value && this.hardcopySEForm.get('workEndTime').value){
        return this.hardcopySEForm.get('workStartTime').value > this.hardcopySEForm.get('workEndTime').value;
    } else return false;
  }

  popSuppDoc(): void{
    const params2 = {
      nric: this.seHardCopyDetails?.headerDto?.nsManNric,
      name: this.seHardCopyDetails?.headerDto?.nsManName,
      caseId: this.seHardCopyDetails?.headerDto?.caseId,
      claimId: this.seHardCopyDetails?.claimId
    }
    const url = this.router.serializeUrl(this.router.createUrlTree(['claim/se-claim/view-supporting-doc'],{queryParams: params2}));
    console.log(url);
