  retrieveOccupationList(): void{
    this.claimService.retrieveOccupationList(null).subscribe(resp => {
      if (resp.body.data) {
        for (const [k, v] of Object.entries(resp.body.data)) {
          this.occupationList.push({
            key: k,
            value: v
          });
        }
        this.occupationList.sort((a:any,b:any)=> {
          if(a.key==='990' || b.key === '990'){
            return 1
          }else{
            return a.value < b.value? -1:1;
          }
          
        });
        this.hardcopySEForm?.get('occupation').setValue(this.occupationList[0].key);
    

      }
      },
      (error: any) => {
        console.log("error is" + String(error?.error?.error_desc));
        this.toastService.error(String(error?.error?.error_desc));
      }
    );
  }

