  openDialog(event: Event, type: string):void{
    console.log("btn is" + type);
    const closedTypeStatus = ["COR", "CNS", "CCO"];
    if(type === 'confirmSubmit' && closedTypeStatus.includes(this.hardcopySEForm.get('itemStatus').value)){
      this.displayRemarks.display = true;
    }else if(type === "confirmBack"){
      console.log("has changes?", this.hasChanged);
      if(this.hasChanged){
        this.nspConfirmService.onConfirmPopupMsg(event, "Claim not saved, are you sure you want to navigate away from page" ,
        type, null,
          () => {
            if(this.seHardCopyDetails?.headerDto?.caseId){
              this.routeOut('data-entry');
            }else{
              this.routeOut('new-se');
            }
          });
      }else{
        if(this.seHardCopyDetails?.headerDto?.caseId){
          this.routeOut('data-entry');
        }else{
          this.routeOut('new-se');
        }
      }
     
    }
    else {
      this.nspConfirmService.onConfirmPopup(event, type, null,
        () => {
  
          switch (type) {
            case 'confirmSave':
              this.saveDraft();
              break;
            case 'confirmSubmit':
              this.submitClaim(null);
              break;
            case 'confirmRemarkAndSubmit':
              this.displayRemarks.display = false;
              this.submitClaim(this.remarksForm.get('closedRejectedRemarks').value);
          }
        }
      );
    }
  }

  saveDraft(): void{
    console.log("saving draft");
    const request = {
      nric: this.newClaimDto?.nric, //non nullable
      coyName: this.hardcopySEForm.get('company')?.value,
      occupationCode: this.hardcopySEForm.get('occupation')?.value,
      otherOccupationDesc: this.hardcopySEForm.get('otherOccupDesc')?.value,
      workStartTime: this.hardcopySEForm.get('workStartTime')?.value,
      workEndTime: this.hardcopySEForm.get('workEndTime')?.value,
      commencementDte: this.dateUtilsService.convertDateToStr(this.hardcopySEForm.get('commencementDte')?.value, this.dateFormat),
      caseId: this.caseId,// nullable caseid
      itemStatusCode: this.hardcopySEForm.get('itemStatus')?.value,
      claimOption: this.hardcopySEForm.get('claimOption').value,
      trgStartDte: this.dateUtilsService.convertDateToStr(this.hardcopySEForm.get('trgStartDte')?.value, this.dateFormat),
      trgEndDte: this.dateUtilsService.convertDateToStr(this.hardcopySEForm.get('trgEndDte')?.value, this.dateFormat),
      suppDocList: this.suppDocList
    }
    this.seClaimService.saveSEHardcopyDraft(request, null).subscribe(resp => {
      if (resp) {
        if (resp.status === 200) {
          const succRespMsg: Map<string, string> = resp.body.data;
          for (const [k, v] of Object.entries(succRespMsg)) {
            this.caseId = v;
            this.claimId = k;
          }
          this.routeOutMsg = resp.body.success_message;
          this.displaySuccessResp.display=true;
          // this.router.navigate(['/claim/se-claim/edit-hardcopy-se', claimId]);

        }
      }
    },
    (error: any) => {
      console.log("error is" + String(error?.error?.error_desc));
      this.toastService.error(String(error?.error?.error_desc));
    });
  }

  submitClaim(closeRejectRemarks: string): void{
    console.log("submit Claim");
    const request = {
      nric: this.newClaimDto?.nric, //non nullable
      coyName: this.hardcopySEForm.get('company')?.value,
      occupationCode: this.hardcopySEForm.get('occupation')?.value,
      otherOccupationDesc: this.hardcopySEForm.get('otherOccupDesc')?.value,
      workStartTime: this.hardcopySEForm.get('workStartTime')?.value,
      workEndTime: this.hardcopySEForm.get('workEndTime')?.value,
      commencementDte: this.dateUtilsService.convertDateToStr(this.hardcopySEForm.get('commencementDte')?.value, this.dateFormat),
      caseId: this.caseId,// nullable caseid
      itemStatusCode: this.hardcopySEForm.get('itemStatus')?.value,
      claimOption: this.hardcopySEForm.get('claimOption').value,
      trgStartDte: this.dateUtilsService.convertDateToStr(this.hardcopySEForm.get('trgStartDte')?.value, this.dateFormat),
      trgEndDte: this.dateUtilsService.convertDateToStr(this.hardcopySEForm.get('trgEndDte')?.value, this.dateFormat),
      suppDocList: this.suppDocList,
      closeRejectRemarks

    }
    this.seClaimService.submitSEHardcopyDraft(request, null).subscribe(resp => {
      if (resp) {
        if (resp.status === 200 && resp.body.data) {
          const succRespMsg: Map<string, string> = resp.body.data;
          for (const [k, v] of Object.entries(succRespMsg)) {
            this.caseId = v;
            this.claimId = k;
          }
          this.routeOutMsg = resp.body.success_message;
          this.displaySuccessResp.display=true;
          // this.router.navigate(['/claim/se-claim/edit-hardcopy-se', claimId]);
        }
      }
    },
    (error: any) => {
      console.log("error is" + String(error?.error?.error_desc));
      this.toastService.error(String(error?.error?.error_desc));
    });
  }

