  submitDocument():void {
    this.suppDocList = [];
    console.log('seSupportingDocuments', this.seSupportingDocuments);

    // txnIdList: this.fileUploadComponent.verifiedFiles.map(x=>
    //   String(x.meta_data.id)
    // )
    this.seSupportingDocuments.verifiedFiles.forEach(file => {
        const claimSuppDoc = new ClaimSuppDoc();
        const metaData = file.meta_data;
        claimSuppDoc.fileName = metaData.fileName;
        claimSuppDoc.indexDt = metaData.createdDtUnix;
        claimSuppDoc.indexByInit = this.currentAccount.userInitials;
        claimSuppDoc.docId = metaData.id;
        console.log("supporting dockey", claimSuppDoc);
        this.suppDocList.push(claimSuppDoc);


    });
    this.toastService.info("Documents will be attached to claim only upon saving/submission of claim");
    this.displaySuppDocDialog.display = false;
}
