                </div>
                <div class = "field grid" *ngIf = "hardcopySEForm?.errors?.['mustNotEqual'] && isNotEmpty(hardcopySEForm.get('workStartTime')?.value)
                && isNotEmpty(hardcopySEForm.get('workEndTime')?.value)">
                    <div class="col-2 col-form-label"></div>
                    <div class="col">
                        <small 
                        class="p-error" icTranslate="nspay2App.claim.validation.CLM-V005"
                        [translateValues]="{ endField: 'Work Hour Start', startField : 'Work Hour End' }"></small>
                    </div>
                </div>
                <div class="field grid">
                    <label for="occupation" class="col-2 col-form-label">Occupation:</label>
                    <div class="col-3">
                        <p-dropdown [options]="occupationList" id="occupation" formControlName="occupation" optionLabel="value" optionValue="key"
                            placeholder="Select" [style]="{'max-width': '14em', 'width':'100%'}"></p-dropdown>
                        <small *ngIf="hardcopySEForm.get('occupation')?.hasError('required') && hardcopySEForm.get('occupation')?.touched"
                            class="p-error" icTranslate="nspay2App.claim.validation.CLM-V001"
                            [translateValues]="{ field: 'Occupation' }">
                        </small>
                    </div>
                    <div class="col-3" *ngIf="hardcopySEForm.get('occupation')?.value==='990'">
                        <input type="text"  pInputText formControlName="otherOccupDesc" id="otherOccupDesc" placeholder="Others Description"  maxlength="60"
                        />
                        <!-- for conditional validators remove istouched criteria, since it needs to fail the form regardless -->
                        <small *ngIf="hardcopySEForm.get('otherOccupDesc').hasError('required')"
                            class="p-error" icTranslate="nspay2App.claim.validation.CLM-V017">
                        </small>
                    </div>

                </div>
                <div class="field grid">
                    <label for="itemStatus" class="col-2 col-form-label">Item Status:</label>
                    <div class="col-3">
                        <p-dropdown [options]="itemStatusList" id="itemStatus" formControlName="itemStatus" optionLabel="name" optionValue="key"
                            placeholder="Select" [style]="{'max-width': '14em', 'width':'100%'}"></p-dropdown>
                        <small *ngIf="hardcopySEForm.get('itemStatus')?.hasError('required') && hardcopySEForm.get('itemStatus')?.touched"
                            class="p-error" icTranslate="nspay2App.claim.validation.CLM-V001"
                            [translateValues]="{ field: 'Item Status' }">
                        </small>
                    </div>
                </div>
                <div class="field grid" *ngIf="hardcopySEForm?.get('claimOption')?.value!=='2'">
                    <label for="company" class="col-2 col-form-label">Company:</label>
                    <div class="col-3">
                        <input type="text" pInputText formControlName="company" id="company"
                        (blur)="setUpperCase('company')" [style]="{'max-width': '14em', 'width':'100%'}"  maxlength="80"/>
                        <small *ngIf="hardcopySEForm?.get('company')?.hasError('required') && hardcopySEForm?.get('company')?.touched"
                        class="p-error" icTranslate="nspay2App.claim.validation.CLM-V001"
                        [translateValues]="{ field: 'Company' }"></small>
                    </div>
                </div>
                <div class="field grid">
                    <label for="commencementDte" class="col-2 col-form-label">Commencement Date:</label>
                    <div class="col-3 input-group">
                        <p-calendar [showIcon]="false" id="commencementDte"
                        dateFormat="dd-mm-yy"
                        formControlName = "commencementDte" appendTo="body"
                        position='center'
                        [style]="{'max-width': '14em'}"
                        ></p-calendar>
                        <small *ngIf="hardcopySEForm?.get('commencementDte')?.hasError('required') && hardcopySEForm?.get('commencementDte')?.touched"
                        class="p-error" icTranslate="nspay2App.claim.validation.CLM-V001"
                        [translateValues]="{ field: 'Commencement Date' }"></small>
                        <!-- <small *ngIf="!dateUtilsService.isValid(hardcopySEForm?.get('commencementDte')?.value) && hardcopySEForm?.get('commencementDte')?.touched"
                        class="p-error" icTranslate="nspay2App.claim.validation.CLM-V002"
                        [translateValues]="{ field: 'Commencement Date' }"></small> -->
                        
                    </div>
                </div>
            </div>
            <!-- Right side -->
            <div class="col-6"  *ngIf="this.isEmpty(caseId); else viewOnlySuppDoc">
                <div class="field grid">
                    <div class="col-7">
                        <h6>Supporting documents: {{ indexTimes }} Times {{suppDocFileForm.controls['supportingDocuments'].value.length}}  Files</h6>
                    </div>
                    <div class="col-5">
                    </div>
                </div>
                <div class = "row" >
                    <p-table [value]="suppDocList"
                    [totalRecords] = "suppDocFileForm.controls['supportingDocuments'].value.length"
                    [(selection)] = "selectedRows"
                    [filterDelay]="0"
                    [selectAll]="selectAll"
                    (selectAllChange)="onSelectAllChange($event)"
                    [loading] = "loading"
                    styleClass="p-datatable-gridlines p-datatable-striped"
                    responsiveLayout="scroll"
                    scrollHeight="20vh"
              
                    #supportDocTable
                    >
                        <ng-template pTemplate="caption">
                            <div class = "col-6  flex justify-content-start gap-2" [style]="{'padding':'0em'}">
                                <button pButton  label="Download" (click) = "download()" [style]="{'margin-right': '.5em', 'margin-bottom': '.5em'}"></button>
                                <button pButton  label="Download All"  (click) = "downloadAll()" [style]="{'margin-right': '.5em', 'margin-bottom': '.5em'}"></button>
                            </div>
                        </ng-template>
                        <ng-template pTemplate="header" let-columns>
                            <tr>
                                <th style="width:3%"><p-tableHeaderCheckbox></p-tableHeaderCheckbox></th>

                                <th>SN</th>
                                <th >eClaim</th>
                                <th pSortableColumn ="fileName">Filename<p-sortIcon field="fileName"></p-sortIcon></th>
                                <th pSortableColumn ="indexDt">Indexed On<p-sortIcon field="indexDt"></p-sortIcon></th>
                                <th pSortableColumn ="indexByInit">Indexed By<p-sortIcon field="indexByInit"></p-sortIcon></th>
                            </tr>
                        </ng-template>
                        <ng-template pTemplate="body" let-suppDocFile let-rowIndex = "rowIndex" let-columns="columns">
                            <tr [pSelectableRow]="suppDocFile">
                                <td><p-tableCheckbox [value]="suppDocFile"></p-tableCheckbox></td>
                                <td>{{ rowIndex + 1}}</td>
                                <td><p-checkbox  [value]="suppDocFile.eClaimInd" [binary]="true" [disabled]="true"></p-checkbox></td>
                                <td>{{ suppDocFile.fileName | defaultString }}</td>
                                <td>{{ this.dateUtilsService.convertUnixToDateString(suppDocFile.indexDt,'DD-MM-YYYY (HHmm)') | defaultString }}</td>
                                <td>{{ suppDocFile.indexByInit | defaultString }}</td>
                            </tr>
                        </ng-template>
                        <ng-template pTemplate="emptymessage">
                            <tr>
                                <td colspan="6"><i class="pi pi-info-circle"></i>&nbsp;No records found.</td>
                            </tr>
                        </ng-template>
                    </p-table>
                </div>
            </div>
            <ng-template #viewOnlySuppDoc>
                <n2-claim-supp-doc [params]="this.params" #claimSuppDocSaved></n2-claim-supp-doc>
            </ng-template>
        </div>
    </form>
</p-panel>
<p-confirmPopup key = "confirmSave" header="Confirmation" icon="fa fa-question-circle" width="425"></p-confirmPopup>
<p-confirmPopup key = "confirmSubmit" header="Confirmation" icon="fa fa-question-circle" width="425"></p-confirmPopup>
<p-confirmPopup key = "confirmBack" header="Confirmation" icon="fa fa-question-circle" width="425"></p-confirmPopup>

<p-dialog header="Upload supporting documents" [(visible)]="displaySuppDocDialog.display" [modal]="true" [style]="{width: '30vw'}"
    [draggable]="false" [resizable]="false">
    <form [formGroup]="suppDocFileForm">
        <n2-file-upload #suppDocFileUpload formControlName='supportingDocuments' id='supportingDocuments'></n2-file-upload>
    </form>
    <ng-template pTemplate="footer">
        <button pButton label="Cancel" type="button" (click)="exitUploadDocModel()" class="p-button-danger"></button>
        <button pButton label="Submit" type="button" (click)="submitDocument()"
        [disabled]="suppDocFileForm.get('supportingDocuments')?.hasError('invalidFileExtension') || 
        suppDocFileUpload.verifiedFiles.length + suppDocFileUpload.selectedFilesObj.length <=0 || suppDocFileUpload.getErrorCount()>0" ></button>
    </ng-template>
</p-dialog>
<p-dialog header="Remarks" [(visible)]="displayRemarks.display" 
[modal]="true" [style]="{width: '50vw'}" [draggable]="false" [resizable]="false" [formGroup]="remarksForm">
    <h6>Please key in remarks for submission of a claim with closed/rejected items</h6>
    <textarea rows="5"
    class="inputfield w-full"
    inputId = "remark"
    formControlName = "closedRejectedRemarks"
    maxlength="450"
    pInputTextarea
    ></textarea>
    <ng-template pTemplate="footer">
        <button pButton label="Cancel" type="button" (click)="displayRemarks.display=false" class="p-button-danger"></button>
        <button pButton label="Submit" type="button" (click)="openDialog($event, 'confirmRemarkAndSubmit')"
        [disabled]="remarksForm.get('closedRejectedRemarks')?.hasError('required')"></button>
    </ng-template>
</p-dialog>
<p-confirmPopup key = "confirmRemarkAndSubmit" header="Confirmation" icon="fa fa-question-circle" width="425"></p-confirmPopup>

<p-dialog [(visible)]="displaySuccessResp.display" 
[modal]="true" [style]="{width: '30vw'}" [draggable]="false" [resizable]="false" 
[closable]="false"	[formGroup]="remarksForm">
    <ng-template pTemplate="header">
        <h2>{{caseId}}</h2>
    </ng-template>
    <h4>{{routeOutMsg}}</h4>
    <ng-template pTemplate="footer">
        <div class ="flex gap-3">
            <button class = "flex-1 align-items-center justify-content-center " pButton label="Data Entry Inbox" type="button" (click)="routeOut('data-entry')"></button>
            <button class = "flex-1 align-items-center justify-content-center " pButton label="New EC" type="button" (click)="routeOut('new-ec')"></button>
            <button class = "flex-1 align-items-center justify-content-center " pButton label="New SE" type="button" (click)="routeOut('new-se')"></button>
        </div>
    </ng-template>

</p-dialog>
