          const cpfSnapshotInfo = this.realtimeCpf.cpfSnapshotInfoDtoList.find(info => (0, moment_1.default)(info.relevantContributionYearMonth, 'YYYY-MM-DD').format('MM-YYYY') === momentStartYrMth.format('MM-YYYY'));
