  loadAWTable(item) {
    // 1st table
    const empClaimItemAWDto = item.empClaimItemAWDto;
    const cpfSnapshotDto = item.cpfSnapshotDto;
    // compute awSnapshot amount (total / divisor)
    let awTotalSnapshotAmt = 0;
    cpfSnapshotDto.cpfSnapshotInfoDtoList.forEach(info => {
      awTotalSnapshotAmt += info.awAmount ? info.awAmount : 0;
    });
    const awSnapshotAmt = awTotalSnapshotAmt / empClaimItemAWDto.snapshotDivisor;
    // table header title
    const prepopulatedStr = cpfSnapshotDto.prepopulatedInd === 'N' ? '' : cpfSnapshotDto.prepopulatedInd === 'Y' ? '(Prepopulated)' : cpfSnapshotDto.prepopulatedInd === 'S' ? '(Self-Initiated)' : '';
    const awStartYearMonth = (0, moment_1.default)(new Date(empClaimItemAWDto.periodYearMonth)).format('MM-YYYY');
    const awEndYearMonth = (0, moment_1.default)(new Date(empClaimItemAWDto.periodEndYearMonth)).format('MM-YYYY');
    const retrievedDate = cpfSnapshotDto.cpfSnapDate ? (0, moment_1.default)(new Date(cpfSnapshotDto.cpfSnapDate)).format('DD-MM-YYYY') : "NA";
    const awTableHeader = awStartYearMonth + ' to ' + awEndYearMonth + ' (' + String(cpfSnapshotDto?.createdByInit) + ')' + ' (' + retrievedDate + ') ' + prepopulatedStr;
    const latestContriMth = cpfSnapshotDto.latestContributionYearMonth ? (0, moment_1.default)(cpfSnapshotDto.latestContributionYearMonth).format('MM-YYYY') : "NA";
    this.selectedAWLatestPeriod = [awStartYearMonth, awEndYearMonth, item?.cpfSnapshotDto?.cpfSnapshotId];
    this.awItemForm = this.formBuilder.group({
      claimItemId: [item?.claimItemId],
      itemStatus: [item?.latestStatus],
      itemName: ['AW'],
      itemType: [item.itemType],
      header: [awTableHeader],
      basis: [item?.apprBasis],
      latestContriMonth: [latestContriMth],
      snapshot: [awSnapshotAmt],
      apprAmt: [item.apprAmt],
      divisorSnapshot: [empClaimItemAWDto.snapshotDivisor],
      divisorUser: [item?.allwDivisor],
      formulaCode: [item.formulaCode],
      formulaLabel: [item.formulaLabel],
      computedFormula: [item.computedFormula],
      computeAmt: [item.computedAmt],
      periodAmt: this.formBuilder.array([])
    });
    // 2nd table
    // Snapshot Column
    const cpfSnapshotInfoDtoList = cpfSnapshotDto.cpfSnapshotInfoDtoList;
    const cpfSnapshotInfoMap = new Map();
    cpfSnapshotInfoDtoList.forEach(ss => {
      cpfSnapshotInfoMap.set((0, moment_1.default)(ss.relevantContributionYearMonth).format('MM-YYYY'), ss);
    });
    // User Column
    const itemInfo = item.empClaimItemInfoDtoList;
    const itemInfoMap = new Map();
    itemInfo.forEach(info => {
      itemInfoMap.set((0, moment_1.default)(info.yrMth).format('MM-YYYY'), info);
    });
    const monthsDiff = (0, moment_1.default)(new Date(empClaimItemAWDto.periodEndYearMonth)).diff((0, moment_1.default)(new Date(empClaimItemAWDto.periodYearMonth)), 'months');
    const periodAmtFormArray = this.awItemForm.get('periodAmt');
    for (let i = 0; i <= monthsDiff; i++) {
      const yrMth = (0, moment_1.default)(new Date(empClaimItemAWDto.periodYearMonth)).add(i, 'months').format('MM-YYYY');
      const rowData = {
        monthYear: yrMth,
        monthSnapshot: cpfSnapshotInfoMap.has(yrMth) ? cpfSnapshotInfoMap.get(yrMth).awAmount : null,
        monthUser: itemInfoMap.has(yrMth) ? itemInfoMap.get(yrMth).amount : null
      };
      periodAmtFormArray.push(this.formBuilder.group(rowData));
      this.awTable.push(rowData);
    }
  }
