  setupInitialItems() {
    const initialEmpItemList = this.initialClaim.initialEmpItemDtoList;
    if (initialEmpItemList.length > 0) {
      initialEmpItemList.forEach(initItem => {
        switch (initItem.itemName.toUpperCase()) {
          case 'ORDINARY WAGE':
            this.initialItemMap.set('ORD', initItem);
            break;
          case 'ADDITIONAL WAGE':
            this.initialItemMap.set('ADD', initItem);
            break;
          case 'DIRECTOR FEE (ANNUAL)':
            this.initialItemMap.set('DIA', initItem);
            break;
          case 'DIRECTOR FEE (MONTHLY)':
            this.initialItemMap.set('DIM', initItem);
            break;
          case 'INTERN':
            this.initialItemMap.set('INT', initItem);
            break;
          case 'OVERSEAS':
            this.initialItemMap.set('OVE', initItem);
            break;
          case 'MINISTRY/STATBOARD':
            this.initialItemMap.set('MIN', initItem);
            break;
          // case 'OLD POLICY-BASIC SALARY':
          //   this.initialItemMap.set('OBS', initItem);
          //   break;
          // case 'OLD POLICY-NON-BASIC SALARY':
          //   this.initialItemMap.set('ONB', initItem);
          //   break;
          default:
            // OTHERS
            this.initialItemMap.set(initItem.itemName, initItem);
            break;
        }
      });
    }
  }
  isSameAsInitialItem(itemType, itemFg, controlName) {
    if (this.initialItemMap.has(itemType) && controlName === 'formulaCode') {
      if (itemFg.get(controlName).value === this.initialItemMap.get(itemType).formulaCode) {
        return true;
      } else {
        return false;
      }
    } else {
      // initialItem don't have item
      return true;
    }
  }
  collapseItem(item) {
    if ((item === 'ORD' || item === 'ADD') && this.itemsCardCollapseMap.has(item)) {
      const itemBoolean = this.itemsCardCollapseMap.get(item);
      if (itemBoolean === true) {
        this.itemsCardCollapseMap.set('ORD', false);
        this.itemsCardCollapseMap.set('ADD', false);
      } else {
        this.itemsCardCollapseMap.set('ORD', true);
        this.itemsCardCollapseMap.set('ADD', true);
      }
    } else {
      const itemBoolean = this.itemsCardCollapseMap.get(item);
      itemBoolean === true ? this.itemsCardCollapseMap.set(item, false) : this.itemsCardCollapseMap.set(item, true);
    }
  }
  changeCollapseIconOnClick(item) {
    return this.itemsCardCollapseMap.get(item) === true ? 'pi pi-minus' : 'pi pi-plus';
  }
  // collapse/expand all
  toggleAllItems(showAll) {
    for (const key of this.itemsCardCollapseMap.keys()) {
      this.itemsCardCollapseMap.set(key, showAll);
    }
    this.showAllItemDetails = showAll;
  }
  getItemStatusDesc(statusCode) {
    return String(this.claimItemStatusMap.get(statusCode));
  }
  getItemName() {
    if (this.selectedItemType) {
      this.itemName = this.itemTypes.find(item => item.value === this.selectedItemType).label;
    }
  }
  //----------------------------
  /*---------- Download Documents ----------*/
  downloadManualComputeDIA(fg) {
    if (fg.get('manualComputeDocId').value) {
      this.downloadDocumentById(fg.get('manualComputeDocId').value);
    }
  }
  downloadManualComputeDIM(fg) {
    if (fg.get('manualComputeDocId').value) {
      this.downloadDocumentById(fg.get('manualComputeDocId').value);
    }
  }
  downloadManualComputeOVE(fg) {
    if (fg.get('manualComputeDocId').value) {
      this.downloadDocumentById(fg.get('manualComputeDocId').value);
    }
  }
  downloadDocumentById(id) {
    this.fileUploadService.downloadTxnId(id).subscribe(res => {
      if (res) {
        const data = res;
        const contentDispositionHeader = data.headers.get('content-disposition');
        const fileName = contentDispositionHeader.split(';')[1].trim().split('=')[1].replace(/"/g, '');
        const blob = new Blob([data.body], {
          type: data.headers.get('content-type')
        });
        const anchor = document.createElement("a");
        anchor.href = URL.createObjectURL(blob);
        anchor.download = fileName;
        anchor.click();
      }
    });
  }
  //----------------------------
  /*---------- Formatting ----------*/
  formatDate(dateValue, format) {
    if (dateValue) {
      return (0, moment_1.default)(new Date(dateValue)).format(format);
    } else {
      return '-';
    }
  }
  formatMonthYear(dateStr, format) {
    if (dateStr && format) {
      return (0, moment_1.default)(dateStr).format(format);
    }
    return 'NA';
  }
  addLeadingZero(str, maxLength) {
    const diff = maxLength - str.length;
    let zeroes = '';
    for (let i = 0; i < diff; i++) {
      zeroes += '0';
    }
    return String(zeroes) + String(str);
  }
  formatTime(time) {
    let result = '--:--';
    if ((0, moment_1.default)(time, ['HH:mm']).isValid()) {
      result = (0, moment_1.default)(time, ['HH:mm']).format('hh:mm A');
    }
    return result;
  }
  setUpperCase(ctrlName) {
    this.claimForm?.get(ctrlName)?.setValue(this.claimForm?.get(ctrlName).value.toUpperCase());
  }
  getDay(controlName) {
    const formControl = this.claimForm.get(controlName);
    if (formControl.value) {
      return (0, moment_1.default)(formControl.value).format('ddd');
    } else {
      return '-';
    }
  }
  //----------------------------
  /*---------- Validation ----------*/
  fallOutsideCurrentYr(date) {
