  /*---------- Claim Section ----------*/
  initialiseFormGroup() {

    const restDayList = this.eClaim?.restDay.map((date: any) => {return moment(String(date), 'YYYY-MM-DD').format('DD-MM-YYYY')});

    this.claimForm = this.formBuilder.group({
      trgPeriodStartDt: [new Date(moment(String(this.eClaim?.claimDto?.trgStartDate), 'YYYY-MM-DD').toDate()), [Validators.required]],
      trgPeriodEndDt: [new Date(moment(String(this.eClaim?.claimDto.trgEndDate), 'YYYY-MM-DD').toDate()), [Validators.required, DateValidator.isBeforeStartDate('trgStartDte', 'trgEndDte')]],
      workHourStart: [this.eClaim?.claimDto?.hrWorkStart, [Validators.required, TimeValidator.wrkHourValidator()]],
      workHourEnd: [this.eClaim?.claimDto?.hrWorkEnd, [Validators.required, TimeValidator.wrkHourValidator()]],
      halfDayInd: [(this.eClaim?.empClaimDto?.halfDayInd === 'Y') ? true : false],
      occupation:  [null],
      commencementDate: [null],
      resignationDate: [null],
      startPayPeriod: [this.eClaim?.empClaimDto?.startPayPeriod, [CommonValidator.numbersOnlyValidator(), CommonValidator.numberRangeValidator(2, 31)]],
      restDay: [restDayList]
    });

    this.outsideCurYr =  this.fallOutsideCurrentYr(this.claimForm?.get('trgPeriodStartDt')?.value)||
    this.fallOutsideCurrentYr(this.claimForm?.get('trgPeriodEndDt')?.value);

    this.initialClaimForm = this.formBuilder.group({
      trgPeriodStartDt: [new Date(moment(String(this.initialClaim?.initialClaimDto?.trgFrDt), 'YYYY-MM-DD').toDate())],
      trgPeriodEndDt: [new Date(moment(String(this.initialClaim?.initialClaimDto.trgToDt), 'YYYY-MM-DD').toDate())],
      workHourStart: [this.initialClaim?.initialClaimDto?.hrWorkStart],
      workHourEnd: [this.initialClaim?.initialClaimDto?.hrWorkEnd],
      halfDayInd: [(this.initialClaim?.initialClaimDto?.halfDayInd === 'Y') ? true : false],
      occupation:  [null],
      commencementDate: [null],
      resignationDate: [null],
      startPayPeriod: [this.addLeadingZero(String(this.initialClaim?.startPayPeriod), 2)],
      restDay: [moment(String(this.initialClaim?.restDay), 'YYYY-MM-DD').format('DD-MM-YYYY')]
    })
  }

  isSameAsInitial(controlName: string) {
    if (this.claimForm.get(controlName).value === this.initialClaimForm.get(controlName).value) {
      return true;
    } else {
      return false;
    }
  }

  isRestDaySame(): boolean {
    if (this.claimForm.get('restDay').value.length === 1) {
      const claimFormRestDay = this.claimForm.get('restDay').value[0];
      if (claimFormRestDay === this.initialClaimForm.get('restDay').value) {
        return true;
      } else {
        return false;
      }
    } else {
      return false;
    }
  }

  getOverlappingClaims(params: HttpParams) {
    this.ecClaimService.getOverlappingEmpClaim(params).subscribe(res => {
        if (res.body.data && res.body.data.length>0) {
          this.overlappingClaims = res.body.data;
          this.overlappingClaims.forEach(x=>{
            let errMsg = "";
            let panelMsg ="";
            panelMsg = errMsg = x.caseId;
            // if (x.claimOption) {
            //   panelMsg = panelMsg.concat("(").concat(x.claimOption).concat(")");
            // }
            if (x.draftByInit) {
              errMsg = errMsg.concat("(").concat("Draft by ").concat(x.draftByInit).concat(")");
            }
            if (x.coyName) {
              panelMsg = panelMsg.concat("(").concat(x.coyName).concat(")");
            }
            x.errorMsg = errMsg;
            x.panelMsg = panelMsg;
          });
          if (!this.claimForm.hasError('overlapping')){
              this.claimForm.setErrors({...this.claimForm.errors || {}, 'overlapping': true});
          }
        } else {
          this.overlappingClaims = [];
          if (this.claimForm?.errors && this.claimForm?.errors['overlapping']) {
            delete this.claimForm.errors['overlapping'];
          }
        }
    });
  }

