  getRealtimeCpf(trgPeriodStartDt: string) {
    let params = new HttpParams();
    params = params.append("trgStartDate", trgPeriodStartDt);
    params = params.append("nric", this.eClaim.claimDto.nric);
    params = params.append("csn", this.eClaim.empClaimDto.csn);
    this.ecClaimService.getRealtimeCpf(params).subscribe(res => {
      if (res.body.data) {
        this.realtimeCpf = res.body.data;
        this.realtimeCpf.cpfSnapshotInfoDtoList.sort((a: any, b: any) => a.relevantContributionYearMonth > b.relevantContributionYearMonth);
        let firstNonNull = false;
        let realtimeDivisor = 0;
        let momentStartYrMth = moment(trgPeriodStartDt, 'YYYY-MM-DD').subtract(11, 'months');
        let momentEndYrMth = moment(trgPeriodStartDt, 'YYYY-MM-DD');
        if (this.selectedAWLatestPeriod) {
          momentStartYrMth = moment(this.selectedAWLatestPeriod[0], 'MM-YYYY');
          momentEndYrMth = moment(this.selectedAWLatestPeriod[1], 'MM-YYYY');
        }
        while(momentStartYrMth.isSameOrBefore(momentEndYrMth)) {
          const cpfSnapshotInfo = this.realtimeCpf.cpfSnapshotInfoDtoList.find((info : CpfSnapshotInfo) =>
            moment(info.relevantContributionYearMonth, 'YYYY-MM-DD').format('MM-YYYY') === momentStartYrMth.format('MM-YYYY'));
          if (cpfSnapshotInfo && cpfSnapshotInfo.awAmount !== null) {
            firstNonNull = true;
            realtimeDivisor += 1;
          } else if (firstNonNull === true) {
            realtimeDivisor += 1;
          }
          momentStartYrMth = momentStartYrMth.add(1, 'months');
        }
        console.log('realtimeDivisor', realtimeDivisor);
        this.realtimeCpf.divisor = realtimeDivisor;

        if (this.owTableRealtime === undefined || this.owTableRealtime === null) {
          this.owTableRealtime = res.body.data;
          this.owTableRealtime.cpfSnapshotInfoDtoList.sort((a: any, b: any) => a.relevantContributionYearMonth > b.relevantContributionYearMonth); 
        }
        if (this.awTableRealtime === undefined || this.awTableRealtime === null) {
          this.awTableRealtime = this.realtimeCpf;
        }
      }
    });
  }

  getOWRealtimeCpf(owStartMonth: string) {
    let params = new HttpParams();
    params = params.append("trgStartDate", owStartMonth);
    this.ecClaimService.getRealtimeCpf(params).subscribe(res => {
      if (res.body.data) {
        this.owTableRealtime = res.body.data;
        this.owTableRealtime.cpfSnapshotInfoDtoList.sort((a: any, b: any) => a.relevantContributionYearMonth > b.relevantContributionYearMonth);
      }
    });
  }

  getRealtimeCpfInfo(yrMth: string): any {
    if (this.realtimeCpf) {
      return this.realtimeCpf.cpfSnapshotInfoDtoList.find((info: any) => moment(new Date(info.relevantContributionYearMonth)).format('MM-YYYY') === yrMth);
    } else {
      return null;
    }
  }

  getRealtimeCpfInfoAmount(yrMth: string, itemType: string): any {
    if (itemType === 'ORD') {
      const owRealtimeInfo = this.owTableRealtime?.cpfSnapshotInfoDtoList.find((info: any) => moment(new Date(info.relevantContributionYearMonth)).format('MM-YYYY') === yrMth);
      if (owRealtimeInfo) {
        return owRealtimeInfo.owAmount;
      } else {
        const realtimeInfo = this.realtimeCpf.cpfSnapshotInfoDtoList.find((info: any) => moment(new Date(info.relevantContributionYearMonth)).format('MM-YYYY') === yrMth); 
        if (realtimeInfo) {
          return realtimeInfo.owAmount;
        }
      }
    } else if (itemType === 'ADD') {
      const awRealtimeInfo = this.awTableRealtime?.cpfSnapshotInfoDtoList.find((info: any) => moment(new Date(info.relevantContributionYearMonth)).format('MM-YYYY') === yrMth);
      if (awRealtimeInfo) {
        return awRealtimeInfo.awAmount;
      } else {
        const realtimeInfo = this.realtimeCpf.cpfSnapshotInfoDtoList.find((info: any) => moment(new Date(info.relevantContributionYearMonth)).format('MM-YYYY') === yrMth); 
        if (realtimeInfo) {
          return realtimeInfo.awAmount;
        }
      }
    }
  }

  computeRealtimeAW(): number {
    if (this.awTableRealtime) {
      let totalAmount = 0;
      this.awTableRealtime.cpfSnapshotInfoDtoList.forEach((info: any) => {
        if (this.awTable.some(rowData => rowData.monthYear === moment(info.relevantContributionYearMonth, 'YYYY-MM-DD').format('MM-YYYY'))) {
          totalAmount += (info.awAmount) ? info.awAmount : 0;
        }
      });
      if (this.awTableRealtime.divisor !== 0) {
        return totalAmount / this.awTableRealtime.divisor;
      } else return null;
    } else if (this.realtimeCpf) {
      let totalAmount = 0;
      this.realtimeCpf.cpfSnapshotInfoDtoList.forEach((info: any) => {
        if (this.awTable.some(rowData => rowData.monthYear === moment(info.relevantContributionYearMonth, 'YYYY-MM-DD').format('MM-YYYY'))) {
          totalAmount += (info.awAmount) ? info.awAmount : 0;
        }
      });
      if (this.realtimeCpf.divisor !== 0) {
        return totalAmount / this.realtimeCpf.divisor;
      } else return null;
    } else {
      return null;
    }
  }

