  loadItems() {
    this.claimItemDetail.forEach((item: ClaimItemDetail) => {
      this.itemsCardCollapseMap.set(item.itemType, true);
      this.selectedBasis = item?.apprBasis;
      switch(item.itemType) {
        case 'ORD': {
          this.loadOWTable(item, false);
          this.currentEClaimItem.set(item.itemType, false);
          break;
        }
        case 'ADD': {
          this.loadAWTable(item);
          this.currentEClaimItem.set(item.itemType, false);
          break;
        }
        case 'DIM': {
          this.loadDIMTable(item);
          this.currentEClaimItem.set(item.itemType, false);
          break;
        }
        case 'DIA': {
          this.loadDIATable(item);
          this.currentEClaimItem.set(item.itemType, false);
          break;
        }
        case 'DIO': {
          this.loadDIOTable(item);
          this.currentEClaimItem.set(item.itemType, false);
          break;
        }
        case 'INT': {
          this.loadINTTable(item);
          this.currentEClaimItem.set(item.itemType, false);
          break;
        }
        case 'MIN': {
          this.loadMINTable(item);
          this.currentEClaimItem.set(item.itemType, false);
          break;
        }
        case 'OVE': {
          this.loadOVETable(item);
          this.currentEClaimItem.set(item.itemType, false);
          break;
        }
        case 'ECO': {
          this.ecoItemList.push(item);
          this.loadECOTable(item);
          const itemKey = 'ECO'+String(item.itemName);
          this.currentEClaimItem.set(itemKey, false);
          break;
        }
        case 'OBS': {
          this.loadOBSTable(item);
          this.currentEClaimItem.set(item.itemType, false);
          break;
        }
        case 'ONB': {
          this.onbItemList.push(item);
          this.loadONBTable(item);
          const itemKey = 'ONB'+String(item.itemName);
          this.currentEClaimItem.set(itemKey, false);
          break;
        }
        case 'AMD': {
          this.loadAMDTable(item);
          this.currentEClaimItem.set(item.itemType, false);
          break;
        }
        default: {
          break;
        }
      }
    });
  }

  loadOWTable(item: ClaimItemDetail, isEdited: boolean) {

    const empClaimItemOWDto = item.empClaimItemOWDto;

    const cpfSnapshotDto = item.cpfSnapshotDto;
    console.log('cpfSnapshotDto', cpfSnapshotDto);
    const cpfSnapshotInfoDto = cpfSnapshotDto.cpfSnapshotInfoDtoList.find(info =>
      moment(empClaimItemOWDto.contriDate).format('MM-YYYY') === moment(info.relevantContributionYearMonth).format('MM-YYYY')
    );
    this.selectedOWLatestMonth = [moment(cpfSnapshotInfoDto.relevantContributionYearMonth).format('MM-YYYY'), cpfSnapshotDto.cpfSnapshotId];
    this.getOWRealtimeCpf(cpfSnapshotInfoDto.relevantContributionYearMonth);

    // table header title
    const prepopulatedStr = (cpfSnapshotDto.prepopulatedInd === 'N') ? '' :
    (cpfSnapshotDto.prepopulatedInd === 'Y') ? '(Prepopulated)' :
    (cpfSnapshotDto.prepopulatedInd === 'S') ? '(Self-Initiated)' : '';
    const retrievedDate = cpfSnapshotDto.cpfSnapDate? moment(cpfSnapshotDto.cpfSnapDate, 'YYYY-MM-DD').format('DD-MM-YYYY') : "NA";
    const owMonth = this.selectedOWLatestMonth[0];
    const owTableHeader = String(owMonth) + ' (' + String(cpfSnapshotDto?.createdByInit) + ')' + ' (' + retrievedDate + ') ' + prepopulatedStr;

    const latestContriMth = cpfSnapshotDto.latestContributionYearMonth ? moment(cpfSnapshotDto.latestContributionYearMonth).format('MM-YYYY') : "NA";
    const owSnapshotAmt = cpfSnapshotInfoDto.owAmount;

    const snapshotTotalAmt = Number(empClaimItemOWDto.basicSalary) + Number(empClaimItemOWDto.fixedAllowance) +
      Number(empClaimItemOWDto.variableAllowance) - Number(empClaimItemOWDto.deduction);
    const userTotalAmt: number = Number(empClaimItemOWDto.basicSalaryMod) + Number(empClaimItemOWDto.fixedAllowanceMod) +
      Number(empClaimItemOWDto.variableAllowanceMod) - Number(empClaimItemOWDto.deductionMod);

    this.owItemForm = this.formBuilder.group({
      claimItemId: [item?.claimItemId],
      itemStatus: [item?.latestStatus],
      itemName: ['OW'],
      itemType: [item.itemType],
      header: [owTableHeader],
      basis: [item?.apprBasis],
      latestContriMonth: [latestContriMth],
      snapshot: [owSnapshotAmt],
      apprAmt: [item.apprAmt],
      bsSnapshot: [empClaimItemOWDto.basicSalary],
      bsUser: [empClaimItemOWDto.basicSalaryMod],
      faSnapshot: [empClaimItemOWDto.fixedAllowance],
      faUser: [empClaimItemOWDto.fixedAllowanceMod],
      vaSnapshot: [empClaimItemOWDto.variableAllowance],
      vaUser: [empClaimItemOWDto.variableAllowanceMod],
      dedSnapshot:[empClaimItemOWDto.deduction],
      dedUser: [empClaimItemOWDto.deductionMod],
      snapshotTotalAmt: [snapshotTotalAmt],
      userTotalAmt: [userTotalAmt],
      formulaCode: [item.formulaCode],
      formulaLabel: [item.formulaLabel],
      computedFormula: [item.computedFormula],
      computeAmt: [item.computedAmt]
    });
  }
