/*---------- Download Documents ----------*/
downloadManualComputeDIA(fg: FormGroup) {
  if (fg.get('manualComputeDocId').value) {
    this.downloadDocumentById(fg.get('manualComputeDocId').value);
  }
}

downloadManualComputeDIM(fg: FormGroup) {
  if (fg.get('manualComputeDocId').value) {
    this.downloadDocumentById(fg.get('manualComputeDocId').value);
  }
}

downloadManualComputeOVE(fg: FormGroup) {
  if (fg.get('manualComputeDocId').value) {
    this.downloadDocumentById(fg.get('manualComputeDocId').value);
  }
}

downloadDocumentById(id: string): void{
  this.fileUploadService.downloadTxnId(id).subscribe((res:any) => {
    if (res) {
        const data = res;
        const contentDispositionHeader: string = data.headers.get('content-disposition');
        const fileName = contentDispositionHeader.split(';')[1].trim().split('=')[1].replace(/"/g, '');
        const blob = new Blob([data.body], { type: data.headers.get('content-type') });
        const anchor = document.createElement("a");
        anchor.href = URL.createObjectURL(blob);
        anchor.download = fileName;
        anchor.click();
    }
  });
}
