/*---------- Formatting ----------*/
  formatDate(dateValue: any, format: string): string{
    if (dateValue) {
      return moment(new Date(dateValue)).format(format);
    } else {
      return '-';
    }
  }

  formatMonthYear(dateStr: string, format: string): string {
    if (dateStr && format) {
      return moment(dateStr).format(format);
    }
    return 'NA';
  }

  addLeadingZero(str: string, maxLength: number): string {
    const diff = maxLength - str.length;
    let zeroes = '';
    for (let i=0; i<diff; i++) {
        zeroes += '0';
    }
    return String(zeroes) + String(str);
  }

  formatTime(time: any): string {
    let result = '--:--';
    if(moment(time,['HH:mm']).isValid()) {
      result = moment(time,['HH:mm']).format('hh:mm A');
    }
    return result;
  }

  setUpperCase(ctrlName: string):void{
    this.claimForm?.get(ctrlName)?.setValue(this.claimForm?.get(ctrlName).value.toUpperCase());
  }

  getDay(controlName: string): string{
    const formControl = this.claimForm.get(controlName);
    if (formControl.value) {
      return moment(formControl.value).format('ddd');
    } else {
      return '-';
    }
  }
  //----------------------------

  /*---------- Validation ----------*/
  fallOutsideCurrentYr(date: any): boolean{
    const curYr = new Date().getFullYear();
    const inputDate = new Date(date);
    if(inputDate.getFullYear()< curYr || inputDate.getFullYear()> curYr){
      return true;
    }else return false;
  }

  isEmpty(input: string): boolean {
    return input===null || input===undefined || input === "";
  }
  //----------------------------

  showErrorMsg(controlName: string, errorName: string): boolean {
    return this.claimForm.get(controlName)?.hasError(errorName) && this.claimForm.get(controlName)?.touched;
  }

