/* eslint-disable @typescript-eslint/no-unsafe-return */

import { Component, OnInit, ViewChild, ViewEncapsulation } from '@angular/core';
import { NspConfirmationService } from 'app/shared/confirm/nsp-confirm.service';
import { CustomToastService } from 'app/layouts/service/customtoast.service';
import { DateUtilsService } from 'app/core/util/date-utils.service';
import { IcCodeService } from 'app/ng-connect';
import { fa0, faRotate, faCircleXmark, faCircleCheck } from '@fortawesome/free-solid-svg-icons';
import { Table } from 'primeng/table';
import { ReportService } from '../service/report.service';
import { IReportType } from '../model/report.model';
import { IQueryGeneratedReport } from '../model/report.model';
import { HttpParams } from '@angular/common/http';

@Component({
  selector: 'n2-query-generated-report',
  templateUrl: './query-generated-report.component.html',
  styleUrls: ['./query-generated-report.component.scss'],
  encapsulation: ViewEncapsulation.None
})
export class QueryGeneratedReportComponent implements OnInit {

  // fontawesome icons
  fa0 = fa0;
  faRotate = faRotate;
  faCircleXmark = faCircleXmark;
  faCircleCheck = faCircleCheck;

  displayEditFavouritesDialog: any = { display: false };

  queriedReports: IQueryGeneratedReport[] = [];

  oneYearAgo: Date;
  reportFrequencies: any[] = [];
  reportCategories: any[] = [];
  reportTypes: IReportType[] = [];

  // Edit Favourites
  existingFavourites: any[] = [];
  selectedFavourites: any[] = [];
  suggestedFavourites: any[] = [];

  // Update Action Taken
  selectedReports: IQueryGeneratedReport[] = [];

  @ViewChild('queryGeneratedReportTable', { static: true })
  private queryGeneratedReportTable!: Table;

  constructor(
    private codeService: IcCodeService,
    private reportService: ReportService,
    private toastService: CustomToastService,
    private nspConfirmationService: NspConfirmationService,
    public dateUtilsService: DateUtilsService
  ) {
  }

  ngOnInit(): void {
    this.load();
  }

  async load(): Promise<void> {
    this.retrieveGeneratedReports();
    this.retrieveReportFrequency();
    this.retrieveReportCategory();
    this.retrieveReportTypes();
    await this.retrieveExistingFavourites();

    this.oneYearAgo = new Date();
    this.oneYearAgo.setFullYear(this.oneYearAgo.getFullYear() - 1);
    this.filterByFavourites();
  }

  retrieveGeneratedReports(): void {
    this.reportService.getGeneratedReports(null).subscribe(resp => {
      if (resp.body.data.dtoList) {
        const dtoList = resp.body.data.dtoList;
        for (let i = 0; i < dtoList.length; i++) {
          dtoList[i].generationDate = this.dateUtilsService.convertUnixToDate(dtoList[i].generationDate);
          dtoList[i].actionDte = dtoList[i].actionDte === 0 ? null : this.dateUtilsService.convertUnixToDate(dtoList[i].actionDte);
        }
        this.queriedReports = dtoList;
        console.log("queried reports", this.queriedReports);
      }
    });  
  }

  retrieveReportFrequency(): void {
    this.codeService.gets('rpt_frequency')
    .subscribe((codes: any[]) => {
      // this.reportFrequencies = codes[0];
      // console.log("reporting", this.reportFrequencies);
      this.reportFrequencies = codes[0].map((x:any)=>({
        code: x.codeId,
        desc: x.label['en-US']
      }));
      console.log("report freq", this.reportFrequencies);
    });
  }

  retrieveReportCategory(): void {
    this.codeService.gets('rpt_category')
    .subscribe((codes: any[]) => {
      // this.reportCategory = codes[0];
      // console.log("reporting", this.reportCategory);
      this.reportCategories = codes[0].map((x:any)=>({
        code: x.codeId,
        desc: x.label['en-US']
      }));
      console.log("report cat", this.reportCategories);
    });
  }

  retrieveReportTypes(): void {
    this.reportService.getAllReportTypes(null).subscribe(resp => {
      if (resp.body.data) {
        this.reportTypes = resp.body.data;
        console.log("all report types", this.reportTypes);
      }
    });
  }

  async retrieveExistingFavourites(): Promise<void> {
    this.existingFavourites = [];
    const resp = await this.reportService.getFavourites(null).toPromise();
    if (resp.body.data) {
      for (const [k, v] of Object.entries(resp.body.data)) {
        this.existingFavourites.push({
          key: k,
          value: v
        });
      }
      console.log("existing favourites", this.existingFavourites);
    }
  }

  exportCSV(): void {
    //
  }

  filterByAll(): void {
    this.queryGeneratedReportTable.clear();
    this.queryGeneratedReportTable.filter(this.oneYearAgo, 'generationDate', 'after');
  }

  filterByMonthly(): void {
    this.queryGeneratedReportTable.clear();
    this.queryGeneratedReportTable.filter('MT', 'frequency', 'contains');
    this.queryGeneratedReportTable.filter(this.oneYearAgo, 'generationDate', 'after');
  }

  filterByQuarterly(): void {
    this.queryGeneratedReportTable.clear();
    this.queryGeneratedReportTable.filter('QT', 'frequency', 'contains');
    this.queryGeneratedReportTable.filter(this.oneYearAgo, 'generationDate', 'after');
  }

  filterByYearly(): void {
    this.queryGeneratedReportTable.clear();
    this.queryGeneratedReportTable.filter('AN', 'frequency', 'contains');
    this.queryGeneratedReportTable.filter(this.oneYearAgo, 'generationDate', 'after');
  }

  filterByFavourites(): void {
    const favList = this.existingFavourites.map(x => x.value);
    console.log("fav list", favList);
    this.queryGeneratedReportTable.clear();
    this.queryGeneratedReportTable.filter(favList, 'templateName', 'in');
    this.queryGeneratedReportTable.filter(this.oneYearAgo, 'generationDate', 'after');
  }

  onOpenEditFavourites(): void {
    this.displayEditFavouritesDialog.display = true;
    this.selectedFavourites = this.existingFavourites;
    console.log("opening dialog...current favourites", this.selectedFavourites);
  }

  onCloseEditFavourites(): void {
    this.displayEditFavouritesDialog.display = false;
    this.selectedFavourites = this.existingFavourites;
    console.log("closing dialog...current favourites", this.selectedFavourites);
  }

  onConfirmFavourites(event: Event, key: string, dialog: any): void {
    console.log("before confirming...selected favourites", this.selectedFavourites);
    this.nspConfirmationService.onConfirmPopup(event, key, dialog, () => {
      const favourites = this.selectedFavourites.map(x => x.key);
      console.log("string of favourites sent to backend", favourites);

      this.reportService.updateFavourites(favourites, null).subscribe(resp => {
        if (resp && resp.status === 200) {
          this.retrieveExistingFavourites();
          console.log("favourites updated successfully");
        }
      },
      (error: any) => {
        console.log("error is " + String(error?.error?.error_desc));
      });
    });
  }

  searchReportType(event: any): void {
    let params = new HttpParams();
    params = params.append('templateName', event.query);

    this.reportService
      .getAllReportTypes({params})
      .subscribe(resp => {
        if (resp.body.data) {
          this.suggestedFavourites = resp.body.data.map((x:any)=>({
            key: x.templateUUID,
            value: x.templateName
          }))
        }
      });
  }

  updateActionTaken(event: Event, key: string): void {
    console.log("selected reports", this.selectedReports);
    this.nspConfirmationService.onConfirmPopup(event, key, null, () => {
      if (this.selectedReports.length > 0) {
        const reportJobInstIds = this.selectedReports.map(x => x.id);
        console.log("report inst ids", reportJobInstIds);
        this.reportService.updateActionTaken(reportJobInstIds, null).subscribe(resp => {
          if (resp && resp.status === 200) {
            console.log("actions updated successfully");        
            this.selectedReports = [];
            this.retrieveGeneratedReports();
            this.toastService.success("Reports Action Taken has been Updated");
          }
        },
        (error: any) => {
          console.log("error is " + String(error?.error?.error_desc));
          this.toastService.error(String(error?.error?.error_desc));
        });
      } else {
        this.toastService.warn("No valid report selected");
      }
    });
  }

}
<p-table #queryGeneratedReportTable
    [value]="queriedReports"
    styleClass="p-datatable-gridlines"
    [paginator]="true"
    [rows]="10"
    [rowsPerPageOptions]="[10,25,50]"
    [showCurrentPageReport]="true"
    currentPageReportTemplate="Showing {first} to {last} of {totalRecords} entries"
    [(selection)]="selectedReports">
    <ng-template pTemplate="caption">
        <div style="display: flex; align-items: center;">
            <button type="button" pButton pRipple icon="pi pi-file" (click)="exportCSV()" pTooltip="Export as CSV"
            tooltipPosition="bottom"></button>
            <button type="button" pButton label="All" (click)="filterByAll()" class="filter-button"></button>
            <button type="button" pButton label="Monthly" (click)="filterByMonthly()" class="filter-button"></button>
            <button type="button" pButton label="Quarterly" (click)="filterByQuarterly()" class="filter-button"></button>
            <button type="button" pButton label="Yearly" (click)="filterByYearly()" class="filter-button"></button>
            <button type="button" pButton label="Favourites" (click)="filterByFavourites()" class="filter-button"></button>
            <button type="button" pButton label="Edit Favourites" (click)="onOpenEditFavourites()"
                class="edit-favourites-button"></button>
            <p-confirmPopup key='updateReportActionTaken'></p-confirmPopup>
            <button type="button" pButton label="Update Action Taken" (click)="updateActionTaken($event, 'updateReportActionTaken')"
                class="update-action-button"></button>
        </div>
    </ng-template>
    <ng-template pTemplate="header">
        <tr>
            <th scope="col" style="width: 1rem;">
                <p-tableHeaderCheckbox></p-tableHeaderCheckbox>
            </th>
            <th scope="col" style="width: 5%">SN</th>
            <th scope="col" pSortableColumn="frequency">Frequency
                <p-sortIcon field="frequency"></p-sortIcon>
                <p-columnFilter
                    type="text" field="frequency" matchMode="in" display="menu"
                    [showMatchModes]="false" [showOperator]="false" [showAddButton]="false">
                    <ng-template pTemplate="header">
                        <div class="px-3 pt-3 pb-0">
                            <span class="font-bold">Frequency</span>
                        </div>
                    </ng-template>
                    <ng-template pTemplate="filter" let-value let-filter="filterCallback">
                        <p-multiSelect [ngModel]="value" [options]="reportFrequencies" display="chip" placeholder="Any"
                            (onChange)="filter($event.value)" optionLabel="desc" optionValue="code">
                            <ng-template let-option pTemplate="item">
                                <span class="ml-1">{{ option.desc }}</span>
                            </ng-template>
                        </p-multiSelect>
                    </ng-template>
                </p-columnFilter>
            </th>
            <th scope="col" pSortableColumn="generationDate">Generation Date
                <p-sortIcon field="generationDate"></p-sortIcon>
                <p-columnFilter type="date" field="generationDate" display="menu"></p-columnFilter>
            </th>
            <th scope="col" pSortableColumn="category">Category
                <p-sortIcon field="category"></p-sortIcon>
                <p-columnFilter
                    type="text" field="category" matchMode="in" display="menu"
                    [showMatchModes]="false" [showOperator]="false" [showAddButton]="false">
                    <ng-template pTemplate="header">
                        <div class="px-3 pt-3 pb-0">
                            <span class="font-bold">Category</span>
                        </div>
                    </ng-template>
                    <ng-template pTemplate="filter" let-value let-filter="filterCallback">
                        <p-multiSelect [ngModel]="value" [options]="reportCategories" display="chip" placeholder="Any"
                            (onChange)="filter($event.value)" optionLabel="desc" optionValue="code">
                            <ng-template let-option pTemplate="item">
                                <span class="ml-1">{{ option.desc }}</span>
                            </ng-template>
                        </p-multiSelect>
                    </ng-template>
                </p-columnFilter>
            </th>
            <th scope="col" pSortableColumn="templateName">Report
                <p-sortIcon field="templateName"></p-sortIcon>
                <p-columnFilter
                    type="text" field="templateName" matchMode="in" display="menu"
                    [showMatchModes]="false" [showOperator]="false" [showAddButton]="false">
                    <ng-template pTemplate="header">
                        <div class="px-3 pt-3 pb-0">
                            <span class="font-bold">Report Type</span>
                        </div>
                    </ng-template>
                    <ng-template pTemplate="filter" let-value let-filter="filterCallback">
                        <p-multiSelect [ngModel]="value" [options]="reportTypes" display="chip" placeholder="Any"
                            (onChange)="filter($event.value)" optionLabel="templateName" optionValue="templateName">
                            <ng-template let-option pTemplate="item">
                                <span class="ml-1">{{ option.templateName }}</span>
                            </ng-template>
                        </p-multiSelect>
                    </ng-template>
                </p-columnFilter>
            </th>
            <th scope="col" pSortableColumn="actionByInit">Actioned By
                <p-sortIcon field="actionByInit"></p-sortIcon>
                <p-columnFilter type="text" field="actionByInit" display="menu"></p-columnFilter>
            </th>
            <th scope="col" pSortableColumn="actionDte">Actioned On
                <p-sortIcon field="actionDte"></p-sortIcon>
                <p-columnFilter type="date" field="actionDte" display="menu"></p-columnFilter>
            </th>
            <th scope="col" pSortableColumn="status">Status
                <p-sortIcon field="status"></p-sortIcon>
                <p-columnFilter type="text" field="status" display="menu"></p-columnFilter>
            </th>
        </tr>
    </ng-template>
    <ng-template pTemplate="body" let-record let-i="rowIndex">
        <tr>
            <td>
                <p-tableCheckbox [value]="record"></p-tableCheckbox>
            </td>
            <td>{{ i + 1 }}</td>
            <td>{{ record.frequencyDesc }}</td>
            <td>{{ record.generationDate | date:'dd-MM-yyyy HH:mm' }}</td>
            <td>{{ record.categoryDesc }}</td>
            <td><a href="">{{ record.templateName }}</a></td>
            <td>{{ record.actionByInit }}</td>
            <td>{{ record.actionDte | date:'dd-MM-yyyy HH:mm' }}</td>
            <td style="text-align: center; vertical-align: middle;">
                <ng-template [ngIf]="record.status === 'A'">
                    <fa-icon [icon]="faCircleCheck"></fa-icon>
                </ng-template>
                <ng-template [ngIf]="record.status === 'F'">
                    <fa-icon [icon]="faCircleXmark"></fa-icon>
                </ng-template>
                <ng-template [ngIf]="record.status === 'E'">
                    <fa-icon [icon]="fa0" [border]="true"></fa-icon>
                </ng-template>
                <ng-template [ngIf]="record.status === 'I'">
                    <fa-icon [icon]="faRotate" [border]="true"></fa-icon>
                </ng-template>
            </td>
        </tr>
    </ng-template>
</p-table>

<p-dialog header="Update Report Favourites Filter" [(visible)]="displayEditFavouritesDialog.display" [modal]="true"
          [style]="{width: '60vw'}" [dismissableMask]="true" [draggable]="false" [resizable]="false">
    <div class="p-fluid">
        <p-autoComplete [(ngModel)]="selectedFavourites" [suggestions]="suggestedFavourites" (completeMethod)="searchReportType($event)"
            field="value" [minLength]="3" [multiple]="true" appendTo="body"></p-autoComplete>
    </div>

    <ng-template pTemplate="footer">
        <button pButton class="p-button-danger" label="Cancel" (click)="onCloseEditFavourites()"
                [style]="{'margin-left': '.5em' , 'margin-bottom': '.5em'}"></button>
        <p-confirmPopup key="confirmEditFavourites"></p-confirmPopup>
        <button pButton label="Submit"
                (click)="onConfirmFavourites($event, 'confirmEditFavourites', displayEditFavouritesDialog)"
                [style]="{'margin-left': '.5em' , 'margin-bottom': '.5em'}"></button>
    </ng-template>
</p-dialog>
