import { Component, OnInit } from '@angular/core';
import { MenuItem } from 'primeng/api';
import { Subscription } from 'rxjs';
import { WorkloadInboxService } from '../service/workload-inbox.service';

@Component({
  selector: 'n2-claim-approver',
  templateUrl: './claim-approver.component.html',
  styleUrls: []
})
export class ClaimApproverComponent implements OnInit {

  public selectedTab: any;
  gfg!: MenuItem[];
  activeItem!: MenuItem;
  private subscription!: Subscription;


  constructor(private workloadService: WorkloadInboxService){
    this.subscription = new Subscription();

  }

  ngOnInit(): void {
    this.getCALabelAndRecords();

  }

  getCALabelAndRecords(): void {
    this.workloadService.getCATabCount(null).subscribe((resp: any) => {
      if (resp.body.data) {
        this.gfg = [
          {
            label: `Approver (${String(resp.body.data.claimApproverList)})`,
            id: "claim-approver",
            // routerLink: ['/approver-inbox-claim-approver'],
            command: (event: Event) => {
              this.selectedTab = "claim-approver";
              console.log("clicked claim-approver tab", event);
            }
          },
          {
            label: `Verifier (${String(resp.body.data.claimVerifierList)})`,
            id: "claim-verifier",

            // routerLink: ['/approver-inbox-claim-verifier'],
            command: (event: Event) => {
              this.selectedTab = "claim-verifier";
              console.log("clicked claim-verifier tab", event);

            }
          },
          {
            label: `SERB (${String(resp.body.data.serbList)})`,
            id: "serb",

            // routerLink: ['/approver-inbox-serb'],
            command: (event: Event) => {
              this.selectedTab = "serb";
              console.log("clicked serb tab", event);

            }
          },
          {
            label: `Appeal (${String(resp.body.data.appealList)})`,
            id: "appeal",

            // routerLink: ['/approver-inbox-appeal'],
            command: (event: Event) => {
              this.selectedTab = "appeal";
              console.log("clicked appeal tab", event);

            }
          }
          
        ];

        this.activeItem = this.gfg[0];
        this.selectedTab = this.activeItem.id;
      }
    });
  }

}


<div class="col-md-12">
    <p-tabMenu [model]="gfg" [activeItem]="activeItem" [style]="{'margin-top': '1em'}"></p-tabMenu>
</div>
<n2-claim-approver-inbox *ngIf="selectedTab === 'claim-approver'" ></n2-claim-approver-inbox>  
<n2-claim-verifier-inbox *ngIf="selectedTab === 'claim-verifier'" ></n2-claim-verifier-inbox>
<n2-serb-inbox *ngIf="selectedTab === 'serb'" ></n2-serb-inbox>
<n2-appeal-inbox *ngIf="selectedTab === 'appeal'" ></n2-appeal-inbox>
