import { HttpParams } from '@angular/common/http';
import { Component, EventEmitter, Input, OnInit, Output, SimpleChanges } from '@angular/core';
import { FormArray, FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { ItemHistory } from 'app/claim/model/ItemHistory.model';
import { DateUtilsService } from 'app/core/util/date-utils.service';
import moment from 'moment';
import { EmpClaimItemHistory } from '../../model/EmpClaimItemHistory.model';


@Component({
    selector: 'n2-item-change-history-section',
    templateUrl: './item-change-history-section.component.html',
    styleUrls: ['./item-change-history-section.component.scss']
  })
export class ItemChangeHistorySectionComponent implements OnInit {
    @Input() itemChangeHistory: EmpClaimItemHistory[];
    @Input() selected:boolean;
    @Output() selectedChange = new EventEmitter<boolean>();

    readonly dateFormat = "DD-MM-YYYY";
    readonly timeFormat = "hhmm";

    itemsCardCollapseMap: Map<string, boolean> = new Map([]);
    owCardCollapseMap: Map<string, boolean> = new Map([]);
    awCardCollapseMap: Map<string, boolean> = new Map([]);

    // items detail
    owItemHistory: EmpClaimItemHistory;
    awItemHistory: EmpClaimItemHistory;
    diaItemHistory: EmpClaimItemHistory;

    owRecords: any[] = [];
    awRecords: any[] = [];
    otherRecords: any[] = [];

    awTableRecords: any[] = [];
    awTable: any[] = [];

    constructor(
        private formBuilder: FormBuilder,
        public dateUtilsService: DateUtilsService
    ) { }

    ngOnInit(): void {
      console.log('itemChangeHistory component checked');
      console.log('itemChangeHistory from child', this.itemChangeHistory);

      if (this.itemChangeHistory) {
        this.loadItemHistory();
      }
    }

    loadItemHistory() {
      this.itemChangeHistory.forEach((item:EmpClaimItemHistory) => {
        const itemHistory = item.itemHistoryDto;
        switch(itemHistory.itemType) {
          case 'ORD': {
            this.itemsCardCollapseMap.set(itemHistory.itemType, true);
            this.owCardCollapseMap.set(item.id, true);
            this.owItemHistory = item;
            this.loadOWTable(item);
            break;
          }
          case 'ADD': {
            this.itemsCardCollapseMap.set(itemHistory.itemType, true);
            this.awCardCollapseMap.set(item.id, true);
            this.awItemHistory = item;
            this.loadAWTable(item);
            break;
          }
          default: {
            this.otherRecords.push(item);
            break;
          }
        }
      });
    }

    loadOWTable(item: EmpClaimItemHistory) {

      const cpfSnapshotDto = item.cpfSnapshotDto;
      const cpfSnapshotInfoDto = (cpfSnapshotDto.cpfSnapshotInfoDtoList.length > 0) ?
        cpfSnapshotDto.cpfSnapshotInfoDtoList[cpfSnapshotDto.cpfSnapshotInfoDtoList.length - 1] : null;
      const owSnapshotAmt = (cpfSnapshotInfoDto) ? Number(cpfSnapshotInfoDto.owAmount) : Number(0);
      item.owSnapshotAmt = owSnapshotAmt;

      // table header title
      const prepopulatedStr = (cpfSnapshotDto.prepopulatedInd === 'N') ? '' :
        (cpfSnapshotDto.prepopulatedInd === 'Y') ? '(Prepopulated)' :
        (cpfSnapshotDto.prepopulatedInd === 'S') ? '(Self-Initiated)' : '';
      const owMonth = moment(new Date(cpfSnapshotDto.cpfSnapDate)).format('MM-YYYY');
      const retrievedDate = moment(new Date(cpfSnapshotInfoDto.cpfSnapDate)).format('DD-MM-YYYY');
      item.owHeaderLabel = 'CPF ' + String(owMonth) + ' (' + String(item.createdByInit) + ')' + ' (' + retrievedDate + ') ' + prepopulatedStr;

      const latestContriMth = moment(new Date(cpfSnapshotInfoDto.latestContributionYearMonth)).format('MM-YYYY');
      item.owLatestContriMth = latestContriMth;

      const empItemOWHistoryDto = item.empItemOWHistoryDto;
      const snapshotTotalAmt = Number(empItemOWHistoryDto?.basicSalary) + Number(empItemOWHistoryDto?.fixedAllowance) +
        Number(empItemOWHistoryDto?.variableAllowance) - Number(empItemOWHistoryDto?.deduction);
      const userTotalAmt: number = Number(empItemOWHistoryDto?.basicSalaryMod) + Number(empItemOWHistoryDto?.fixedAllowanceMod) +
        Number(empItemOWHistoryDto?.variableAllowanceMod) - Number(empItemOWHistoryDto?.deductionMod);
      empItemOWHistoryDto.totalAmount = snapshotTotalAmt;
      empItemOWHistoryDto.totalAmountMod = userTotalAmt;

      this.owRecords.push(item);
    }

    loadAWTable(item: EmpClaimItemHistory) {

      const cpfSnapshotDto = item.cpfSnapshotDto;
      const cpfSnapshotInfoDto = (cpfSnapshotDto.cpfSnapshotInfoDtoList.length > 0) ?
        cpfSnapshotDto.cpfSnapshotInfoDtoList[cpfSnapshotDto.cpfSnapshotInfoDtoList.length - 1] : null;
      const awSnapshotAmt = (cpfSnapshotInfoDto) ? Number(cpfSnapshotInfoDto.awAmount) : Number(0);
      item.awSnapshotAmt = awSnapshotAmt;

      // table header title
      const prepopulatedStr = (cpfSnapshotDto.prepopulatedInd === 'N') ? '' :
        (cpfSnapshotDto.prepopulatedInd === 'Y') ? '(Prepopulated)' :
        (cpfSnapshotDto.prepopulatedInd === 'S') ? '(Self-Initiated)' : '';
      const awStartYearMonth = moment(new Date(cpfSnapshotDto.startYearMonth)).format('MM-YYYY');
      const awEndYearMonth = moment(new Date(cpfSnapshotDto.endYearMonth)).format('MM-YYYY');
      const retrievedDate = moment(new Date(cpfSnapshotInfoDto.cpfSnapDate)).format('DD-MM-YYYY');
      item.awHeaderLabel = 'CPF ' + awStartYearMonth + ' to ' + awEndYearMonth + ' (' + String(item.createdByInit) + ')' + ' (' + retrievedDate + ') ' + prepopulatedStr;

      const latestContriMth = moment(new Date(cpfSnapshotInfoDto.latestContributionYearMonth)).format('MM-YYYY');
      item.awLatestContriMth = latestContriMth;

      // 2nd table
      const cpfSnapshotInfoDtoList = cpfSnapshotDto.cpfSnapshotInfoDtoList;
      this.awTable = [];
      if (cpfSnapshotInfoDtoList.length > 12) {
        const startMonth = cpfSnapshotInfoDtoList.length - 12;
        for (let i=startMonth; i < cpfSnapshotInfoDtoList.length; i++) {
          this.awTable.push(cpfSnapshotInfoDtoList[i]);
        }
      } else {
        for (let i=0; i < cpfSnapshotInfoDtoList.length; i++) {
          this.awTable.push(cpfSnapshotInfoDtoList[i]);
        }
      }

      this.awRecords.push(item);
      this.awTableRecords.push(this.awTable);
    }

    getAwTable(arrayIndex: number): any {
      return this.awTableRecords[arrayIndex];
    }

    collapseItem(item: string, id: string = null) {
      if (item === 'ORD') {
        const itemBoolean = this.owCardCollapseMap.get(id);
        (itemBoolean === true) ? this.owCardCollapseMap.set(id, false) : this.owCardCollapseMap.set(id, true);
      }
      if (item === 'ADD') {
        const itemBoolean = this.awCardCollapseMap.get(id);
        (itemBoolean === true) ? this.awCardCollapseMap.set(id, false) : this.awCardCollapseMap.set(id, true);
      }
      // const itemBoolean = this.itemsCardCollapseMap.get(item);
      // (itemBoolean === true) ? this.itemsCardCollapseMap.set(item, false) : this.itemsCardCollapseMap.set(item, true);
    }

    formatUnixToDate(value: any, format:string):string {
        return this.dateUtilsService.convertUnixToDateString(value, format);
    }

    formatDate(value: any, format:string):string {
      return moment(value).format(format);
    }

    accordionClick(): void{
      this.selectedChange.emit(this.selected);
    }

    consoleLog() {
        console.log('itemChangeHistory', this.itemChangeHistory);
        console.log('itemsCardCollapseMap', this.itemsCardCollapseMap);
    }

    changeCollapseIconOnClick(item: string): string {
      return this.itemsCardCollapseMap.get(item) === true ? 'pi pi-minus' : 'pi pi-plus';
    }

}
<p-accordionTab [(selected)]="selected" (click)="accordionClick()">
    <ng-template pTemplate="header" >
        <div class = "grid col-6 align-items-center" style = "margin: 0 0; padding:0 0">
            <div class="col-12">
                Item Change History
            </div>
        </div>
    </ng-template>
    <div class="grid" *ngIf="owRecords.length > 0 || awRecords.length > 0 || otherRecords.length > 0; else emptymessage">
        <!-- OW START -->
        <div class="col-6" *ngIf="itemsCardCollapseMap.has('ORD')">
            <p-card  [style]="{'height': '100%'}">
                <div *ngFor="let item of owRecords; let i = index">
                    <div class="grid">
                        <div class="col-2">
                            <button pButton pRipple type="button" [icon]="changeCollapseIconOnClick('ORD')" class="p-button-rounded p-button-text" (click)="collapseItem('ORD', item.id)"></button>
                            <span>
                                <label style="margin-bottom:.5rem"><h4>OW</h4></label>
                            </span>
                        </div>
                        <div class="col-3">
                            Appr Amt: {{item?.itemHistoryDto?.apprAmt | currency}}
                        </div>
                        <div class="col-3">
                            Formula: {{item?.itemHistoryDto?.formulaLabel}}
                        </div>
                    </div>
                    <div class="grid">
                        <div class="col">
                            Computation: {{item?.itemHistoryDto?.computedFormula}} = {{item?.itemHistoryDto?.computedAmt}}
                        </div>
                    </div>
                    <div class="row">&nbsp;</div> <!-- breakline -->
                    <div class="grid">
                        <div class="col-6">
                            Verifier: STRTHRU(DD-MM-YYYY (HHMM))
                        </div>
                        <div class="col-6">
                            Approver: STRTHRU(DD-MM-YYYY (HHMM))
                        </div>
                    </div>
                    <span *ngIf="owCardCollapseMap.get(item.id)">
                        <hr>
                        <div class="grid">
                            <div class="col-6">
                                <p-table [value]="[[]]" styleClass="p-datatable-gridlines" columnResizeMode="expand">
                                    <ng-template pTemplate="header">
                                        <tr>
                                            <th scope="colgroup" colspan="4" class="text-center">{{item?.owHeaderLabel}}</th>
                                        </tr>
                                    </ng-template>
                                    <ng-template pTemplate="body" let-rowData>
                                        <tr>
                                            <td colspan="2" [ngStyle]="{backgroundColor:'var(--gray-50)'}">Latest Contribution Month</td>
                                            <td colspan="2">{{ item?.owLatestContriMth }}</td>
                                        </tr>
                                        <tr>
                                            <td [ngStyle]="{backgroundColor:'var(--gray-50)'}"></td>
                                            <td class="text-center" [ngStyle]="{backgroundColor:'var(--gray-50)'}">Snapshot</td>
                                            <td class="text-center" [ngStyle]="{backgroundColor:'var(--gray-50)'}">Appr Amt</td>
                                            <td rowspan="2" [ngStyle]="{backgroundColor:'var(--gray-50)'}">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</td>
                                        </tr>
                                        <tr>
                                            <td [ngStyle]="{backgroundColor:'var(--gray-50)'}">OW</td>
                                            <td>{{ item?.owSnapshotAmt | currency}}</td>
                                            <div *ngIf="item?.owSnapshotAmt !== item?.itemHistoryDto?.apprAmt; then owRedAlert else owNormal"></div>
                                                <ng-template #owRedAlert><td class="text-danger font-semibold">{{ item?.itemHistoryDto?.apprAmt | currency }}</td></ng-template>
                                                <ng-template #owNormal><td>{{ item?.itemHistoryDto?.apprAmt | currency}}</td></ng-template>
                                        </tr>
                                        <tr>
                                            <td colspan="4" class="text-center font-medium" [ngStyle]="{backgroundColor:'var(--gray-50)'}">Breakdown</td>
                                        </tr>
                                        <tr>
                                            <td [ngStyle]="{backgroundColor:'var(--gray-50)'}"></td>
                                            <td class="text-center" [ngStyle]="{backgroundColor:'var(--gray-50)'}">Snapshot</td>
                                            <td class="text-center" [ngStyle]="{backgroundColor:'var(--gray-50)'}">User</td>
                                            <td rowspan="6" [ngStyle]="{backgroundColor:'var(--gray-50)'}"></td>
                                        </tr>
                                        <tr>
                                            <td [ngStyle]="{backgroundColor:'var(--gray-50)'}">BS</td>
                                            <td>{{ item?.empItemOWHistoryDto?.basicSalary | currency }}</td>
                                            <td>{{ item?.empItemOWHistoryDto?.basicSalaryMod | currency }}</td>
                                        </tr>
                                        <tr>
                                            <td [ngStyle]="{backgroundColor:'var(--gray-50)'}">FA</td>
                                            <td>{{ item?.empItemOWHistoryDto?.fixedAllowance | currency }}</td>
                                            <td>{{ item?.empItemOWHistoryDto?.fixedAllowanceMod | currency }}</td>
                                        </tr>
                                        <tr>
                                            <td [ngStyle]="{backgroundColor:'var(--gray-50)'}">VA</td>
                                            <td>{{ item?.empItemOWHistoryDto?.variableAllowance | currency }}</td>
                                            <td>{{ item?.empItemOWHistoryDto?.variableAllowanceMod | currency }}</td>
                                        </tr>
                                        <tr>
                                            <td [ngStyle]="{backgroundColor:'var(--gray-50)'}">DED</td>
                                            <td>{{ item?.empItemOWHistoryDto?.deduction | currency }}</td>
                                            <td>{{ item?.empItemOWHistoryDto?.deductionMod | currency }}</td>
                                        </tr>
                                        <tr>
                                            <td [ngStyle]="{backgroundColor:'var(--gray-50)'}">Total</td>
                                            <td>{{ item?.empItemOWHistoryDto?.totalAmount | currency }}</td>
                                            <td>{{ item?.empItemOWHistoryDto?.totalAmountMod | currency }}</td>
                                        </tr>
                                    </ng-template>
                                </p-table>
                            </div>
                        </div>
                    </span>
                    <div class="col-12" *ngIf="owRecords.length > 1"><hr></div>
                </div>
            </p-card>
        </div>
        <!-- OW END -->
        <!-- AW START -->
        <div class="col-6" *ngIf="itemsCardCollapseMap.has('ADD')">
            <p-card [style]="{'height': '100%'}">
                <div *ngFor="let item of awRecords; let i = index">
                    <div class="grid">
                        <div class="col-2">
                            <button pButton pRipple type="button" [icon]="changeCollapseIconOnClick('ADD')" class="p-button-rounded p-button-text" (click)="collapseItem('ADD', item.id)"></button>
                            <span>
                                <label style="margin-bottom:.5rem"><h4>AW</h4></label>
                            </span>
                        </div>
                        <div class="col-3">
                            Appr Amt: {{item?.itemHistoryDto?.apprAmt | currency}}
                        </div>
                        <div class="col-3">
                            Formula: {{item?.itemHistoryDto?.formulaLabel}}
                        </div>
                    </div>
                    <div class="grid">
                        <div class="col">
                            Computation: {{item?.itemHistoryDto?.computedFormula}} = {{item?.itemHistoryDto?.computedAmt}}
                        </div>
                    </div>
                    <div class="row">&nbsp;</div> <!-- breakline -->
                    <div class="grid">
                        <div class="col-6">
                            Verifier: STRTHRU(DD-MM-YYYY (HHMM))
                        </div>
                        <div class="col-6">
                            Approver: STRTHRU(DD-MM-YYYY (HHMM))
                        </div>
                    </div>
                    <span *ngIf="awCardCollapseMap.get(item.id)">
                        <hr>
                        <div class="grid">
                            <div class="col-6">
                                <p-table [value]="[[]]" styleClass="p-datatable-gridlines">
                                    <ng-template pTemplate="header">
                                        <tr>
                                            <th scope="colgroup" colspan="4" class="text-center">{{ item?.awHeaderLabel }}</th>
                                        </tr>
                                    </ng-template>
                                    <ng-template pTemplate="body" let-products>
                                        <tr>
                                            <td colspan="2" [ngStyle]="{backgroundColor:'var(--gray-50)'}">Latest Contribution Month</td>
                                            <td colspan="2">{{item?.awLatestContriMth}}</td>
                                        </tr>
                                        <tr>
                                            <td [ngStyle]="{backgroundColor:'var(--gray-50)'}"></td>
                                            <td class="text-center" [ngStyle]="{backgroundColor:'var(--gray-50)'}">Snapshot</td>
                                            <td class="text-center" [ngStyle]="{backgroundColor:'var(--gray-50)'}">Appr Amt</td>
                                            <td rowspan="3" [ngStyle]="{backgroundColor:'var(--gray-50)'}">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</td>
                                        </tr>
                                        <tr>
                                            <td [ngStyle]="{backgroundColor:'var(--gray-50)'}">AW</td>
                                            <td>{{item?.awSnapshotAmt | currency}}</td>
                                            <div *ngIf="item?.awSnapshotAmt !== item?.itemHistoryDto?.apprAmt; then awRedAlert else awNormal"></div>
                                            <ng-template #awRedAlert><td class="text-danger font-semibold">{{ item?.itemHistoryDto?.apprAmt | currency }}</td></ng-template>
                                            <ng-template #awNormal><td>{{ item?.itemHistoryDto?.apprAmt | currency}}</td></ng-template>
                                        </tr>
                                        <tr>
                                            <td [ngStyle]="{backgroundColor:'var(--gray-50)'}">Divisor</td>
                                            <td>{{item?.empItemAWHistoryDto?.divisor}}</td>
                                            <td>{{item?.empItemAWHistoryDto?.divisor}}</td>
                                        </tr>
                                    </ng-template>
                                </p-table>

                                <div class="row">&nbsp;</div> <!-- breakline -->

                                <p-table [value]="getAwTable(i)" styleClass="p-datatable-gridlines">
                                    <ng-template pTemplate="header">
                                        <tr>
                                            <th scope="col"></th>
                                            <th scope="col" class="text-center">Snapshot</th>
                                            <th scope="col" class="text-center">User</th>
                                            <th scope="col" [ngStyle]="{backgroundColor:'var(--gray-50)'}">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</th>
                                        </tr>
                                    </ng-template>
                                    <ng-template pTemplate="body" let-rowData>
                                        <tr>
                                            <td [ngStyle]="{backgroundColor:'var(--gray-50)'}">{{formatDate(rowData?.relevantContributionYearMonth, 'MM-YYYY')}}</td>
                                            <td>{{rowData?.awAmount | currency}}</td>
                                            <td>{{rowData?.awAmount | currency}}</td>
                                            <td [ngStyle]="{backgroundColor:'var(--gray-50)'}">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</td>
                                        </tr>
                                    </ng-template>
                                </p-table>
                            </div>
                        </div>
                    </span>
                    <div class="col-12" *ngIf="awRecords.length > 1"><hr></div>
                </div>
            </p-card>
        </div>
        <!-- ADD END -->
        <!-- OTHER ITEMS START -->
        <p-card class="col-6" *ngFor="let item of otherRecords; let i = index" [style]="{'margin-bottom': '1rem'}">
            <div class="grid">
                <div class="col-6">
                    <span>
                        <label style="margin-bottom:.5rem"><h4>{{item?.itemHistoryDto?.itemName}}</h4></label>
                    </span>
                </div>
                <div class="col-3">
                    Appr Amt: {{item?.itemHistoryDto?.apprAmt | currency}}
                </div>
                <div class="col-3">
                    Formula: {{item?.itemHistoryDto?.formulaLabel}}
                </div>
            </div>
            <div class="grid">
                <div class="col">
                    Computation: {{item?.itemHistoryDto?.computedFormula}} = {{item?.itemHistoryDto?.computedAmt}}
                </div>
            </div>
            <div class="row">&nbsp;</div>
            <div class="grid">
                <div class="col-6">
                    Verifier: STRTHRU(DD-MM-YYYY (HHMM))
                </div>
                <div class="col-6">
                    Approver: STRTHRU(DD-MM-YYYY (HHMM))
                </div>
            </div>
        </p-card>
        <!-- OTHER ITEMS END -->
    </div>
    <ng-template #emptymessage>
        <span><i class="pi pi-info-circle"></i>&nbsp;
            No records found.</span>
     </ng-template>
    <div class = "grid col flex justify-content-end">
        <button pButton label="ICH LOG" [style]="{'margin-bottom': '.5em', 'margin-left': '.5em'}" (click)="consoleLog()" (click)="$event.stopPropagation()"></button> <!-- TODO: remove -->
    </div>
</p-accordionTab>
