import { Component, OnInit, OnDestroy } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { HttpResponse } from '@angular/common/http';
import { Subscription, combineLatest } from 'rxjs';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';
import { AlertService } from 'app/core/util/alert.service';
import { EventManager } from 'app/core/util/event-manager.service';
import { ParseLinks } from 'app/core/util/parse-links.service';
import { Group } from './group.model';
import { GroupService } from './group.service';
import { ITEMS_PER_PAGE } from 'app/config/pagination.constants';
import { AccountService } from 'app/core/auth/account.service';
import { GroupDeleteDialogComponent } from 'app/admin/access-control';

@Component({
  selector: 'ic-group',
  templateUrl: './group.component.html',
})
export class GroupComponent implements OnInit, OnDestroy {
  currentAccount: any;
  groups: Group[] = [];
  error: any;
  success: any;
  eventSubscriber?: Subscription;
  routeData: any;
  links: any;
  totalItems: any;
  queryCount: any;
  itemsPerPage = ITEMS_PER_PAGE;
  page!: number;
  ascending!: boolean;
  predicate!: string;
  previousPage: any;
  reverse: any;
  searchCriteria: any;

  constructor(
    private groupService: GroupService,
    private parseLinks: ParseLinks,
    private alertService: AlertService,
    private accountService: AccountService,
    private activatedRoute: ActivatedRoute,
    private router: Router,
    private eventManager: EventManager,
    protected modalService: NgbModal
  ) {
    this.searchCriteria = {
      groupId: '',
      groupName: '',
    };
  }

  loadAll(): void {
    this.groupService
      .query({
        groupId: this.searchCriteria.groupId,
        groupName: this.searchCriteria.groupName,
        page: this.page - 1,
        size: this.itemsPerPage,
        sort: this.sort(),
      })
      .subscribe(
        (res: HttpResponse<any>) => this.onSuccess(res.body, res.headers),
        (res: HttpResponse<any>) => this.onError(res.body)
      );
  }
  loadPage(page: number): void {
    if (page !== this.previousPage) {
      this.previousPage = page;
      this.transition();
    }
  }
  transition(): void {
    this.router.navigate(['admin/acm/ic-group'], {
      queryParams: {
        page: this.page,
        size: this.itemsPerPage,
        sort: this.predicate + ',' + (this.reverse ? 'asc' : 'desc'),
      },
    });
  }

  search(): void {
    this.links = {
      last: 0,
    };
    this.page = 0;
    this.predicate = 'groupId';
    this.reverse = false;
    this.loadAll();
  }

  clear(): void {
    this.searchCriteria = {
      groupId: '',
      groupName: '',
    };
    this.page = 0;
    this.router.navigate([
      'admin/acm/ic-group',
      {
        page: this.page,
        sort: this.predicate + ',' + (this.reverse ? 'asc' : 'desc'),
      },
    ]);
  }
  ngOnInit(): void {
    this.accountService.identity().subscribe(account => (this.currentAccount = account));
    this.handleNavigation();
    this.registerChangeInGroups();
  }

  ngOnDestroy(): void {
    if (this.eventSubscriber) {
      this.eventManager.destroy(this.eventSubscriber);
    }
  }

  trackId(index: number, item: Group): any {
    return item.groupId;
  }
  registerChangeInGroups(): void {
    this.eventSubscriber = this.eventManager.subscribe('groupListModification', () => this.loadAll());
  }

  sort(): any {
    const result = [this.predicate + ',' + (this.reverse ? 'asc' : 'desc')];
    if (this.predicate !== 'groupId') {
      result.push('groupId');
    }
    return result;
  }

  delete(group: Group): void {
    this.groupService.findAllAssignedRoles(group.groupId).subscribe((res: HttpResponse<any>) => {
      if (res.body.length !== 0 || group.subject2Groups?.length !== 0) {
        this.alertService.addAlert({ type: 'danger', translationKey: 'acmAdmin.group.delete.error' });
        return;
      }
      const modalRef = this.modalService.open(GroupDeleteDialogComponent, { size: 'lg', backdrop: 'static' });
      modalRef.componentInstance.group = group;
    });
  }

  private handleNavigation(): void {
    combineLatest([this.activatedRoute.data, this.activatedRoute.queryParamMap]).subscribe(([data, params]) => {
      const page = params.get('page');
      this.page = page !== null ? +page : 1;
      const sort = (params.get('sort') ?? data['defaultSort']).split(',');
      this.predicate = sort[0];
      this.ascending = sort[1] === 'asc';
      this.loadAll();
    });
  }

  private onSuccess(data: any, headers: any): void {
    this.links = this.parseLinks.parse(headers.get('link'));
    this.totalItems = headers.get('X-Total-Count');
    this.queryCount = this.totalItems;
    this.groups = data;
  }

  private onError(error: any): void {
    this.alertService.addAlert({ type: 'danger', translationKey: error.message });
  }
}
<div>
  <h2>
    <span icTranslate="acmAdmin.group.home.title">Groups</span>
  </h2>
  <ic-alert></ic-alert>
  <br />
  <div class="row">
    <form name="searchForm" class="col-sm-12">
      <div class="card">
        <div class="card-header"><span icTranslate="acmAdmin.home.SearchCriteria">Search Criteria</span></div>
        <div class="card-block" style="padding: 1.5%">
          <div class="rounded-pill row">
            <label class="col-sm-2 form-control-label">Group Id</label>
            <div class="col-sm-4">
              <input type="text" class="form-control" name="groupId" id="groupId" [(ngModel)]="searchCriteria.groupId" />
            </div>
          </div>

          <div class="rounded-pill row">
            <label class="col-sm-2 form-control-label">Group Name</label>
            <div class="col-sm-4">
              <input type="text" class="form-control" name="groupName" id="groupName" [(ngModel)]="searchCriteria.groupName" />
            </div>
          </div>

          <div style="margin-top: 1.5%">
            <button id="searchGroup" type="button" class="btn btn-warning btn-sm" (click)="search()">
              <fa-icon [icon]="'search'"></fa-icon>
              <span class="fa fa-search" icTranslate="acmAdmin.home.search"></span>
            </button>
            <button type="button" class="btn btn-secondary btn-sm" (click)="clear()">
              <fa-icon [icon]="'trash-alt'"></fa-icon>
              <span class="fa fa-trash-o" icTranslate="acmAdmin.home.reset"></span>
            </button>

            <button id="createNewGroupButton" class="btn btn-primary btn-sm" [routerLink]="['../ic-group-new']">
              <fa-icon [icon]="'plus'"></fa-icon>
              <span icTranslate="acmAdmin.group.home.createLabel"> Create new Group </span>
            </button>
          </div>
        </div>
      </div>
    </form>
  </div>
  <br />
  <div class="card">
    <div class="card-header"><span icTranslate="acmAdmin.home.SearchResult">Search Criteria</span></div>
    <div class="card-block" style="padding: 1.5% 1.5% 0 1.5%">
      <div class="table-responsive" *ngIf="groups">
        <table class="table table-striped">
          <thead>
            <tr icSort [(predicate)]="predicate" [(ascending)]="reverse" [callback]="transition.bind(this)">
              <th scope="col"><span icTranslate="acmAdmin.home.sn">S/No.</span></th>
              <th scope="col" icSortBy="groupId"><span icTranslate="acmAdmin.group.groupId">Group Id</span> <fa-icon [icon]="'sort'"></fa-icon></th>
              <th scope="col" icSortBy="groupName">
                <span icTranslate="acmAdmin.group.groupName">Group Name</span> <fa-icon [icon]="'sort'"></fa-icon>
              </th>
              <th scope="col"><span icTranslate="acmAdmin.group.noOfUser">No. of User</span></th>
              <th scope="col" icSortBy="updatedBy">
                <span icTranslate="acmAdmin.group.updatedBy">Updated By</span> <fa-icon [icon]="'sort'"></fa-icon>
              </th>
              <th scope="col" icSortBy="updatedDt">
                <span icTranslate="acmAdmin.group.updatedDt">Updated Date</span> <fa-icon [icon]="'sort'"></fa-icon>
              </th>
              <th scope="col" style="text-align: center">
                <span icTranslate="acmAdmin.home.operation">Operation</span>
              </th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let group of groups; let idx = index; trackBy: trackId">
              <td>{{ idx + 1 }}</td>
              <td>{{ group.groupId }}</td>
              <td>{{ group.groupName }}</td>
              <td>{{ group.subject2Groups?.length! }}</td>
              <td>{{ group.updatedBy }}</td>
              <td>{{ group.updatedDt | date: 'yyyy-MM-dd' }}</td>
              <td class="text-center">
                <div class="btn-group flex-btn-group-container">
                  <button type="submit" [routerLink]="['../../../admin/acm/ic-group', group.groupId]" class="btn btn-primary btn-sm">
                    <fa-icon [icon]="'pencil-alt'"></fa-icon>
                    <span class="hidden-md-down" icTranslate="entity.action.edit">Edit</span></button
                  >&nbsp;
                  <button
                    [id]="group.groupId"
                    type="submit"
                    (click)="delete(group)"
                    class="btn btn-danger btn-sm"
                    data-bs-cy="entityDeleteButton"
                  >
                    <fa-icon icon="times"></fa-icon>
                    <span class="hidden-md-down" icTranslate="entity.action.delete">Delete</span>
                  </button>
                </div>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
      <div *ngIf="groups && groups.length">
        <div class="row justify-content-center">
          <ic-item-count [params]="{ page: page, totalItems: totalItems, itemsPerPage: itemsPerPage }"></ic-item-count>
        </div>
        <div class="row justify-content-center">
          <ngb-pagination
            [collectionSize]="totalItems"
            [pageSize]="itemsPerPage"
            [(page)]="page"
            (pageChange)="loadPage(page)"
          ></ngb-pagination>
        </div>
      </div>
    </div>
  </div>
</div>
