import {Component, Inject, LOCALE_ID, OnInit, ViewChild} from '@angular/core';
import {ActivatedRoute} from "@angular/router";
import {DateUtilsService} from "app/core/util/date-utils.service";
import {ExportUtilsService} from "app/core/util/export-utils.service";
import {HttpParams} from "@angular/common/http";
import {ViewPastClaim} from "app/claim/model/view-past-claim.model";
import {ClaimService} from "app/claim/service/claim.service";
import {Table} from "primeng/table";
import {formatDate} from "@angular/common";

@Component({
  selector: 'n2-view-past-claim',
  templateUrl: './view-past-claim.component.html',
  styleUrls: ['./view-past-claim.component.scss']
})
export class ViewPastClaimComponent implements OnInit {

  params: any;
  viewPastClaims: ViewPastClaim[] = [];
  checkBoxValue = false;

  @ViewChild('dt') dt: Table;

  constructor(
    private route: ActivatedRoute,
    private claimService: ClaimService,
    public dateUtilsService: DateUtilsService,
    private exportUtilsService: ExportUtilsService,
    @Inject(LOCALE_ID) protected localeId: string) {
  }

  ngOnInit(): void {
    this.route.queryParams.subscribe((params) => {
      this.params = params;

      let httpParams = new HttpParams();
      httpParams = httpParams.append("nric",this.params.nric);
      httpParams = httpParams.append("caseId",this.params.caseId);

      this.claimService.retrieveViewPastClaims(httpParams).subscribe((res) => {
        if(res.data){
          this.viewPastClaims = res.data;
          if(this.params.csn){
            this.checkBoxValue = true;
            this.filterCoy(this.dt);
          }
        }
      })

    });
  }

  popUpEnquireClaim(claimId: string): void{
    //TODO: popup for enquiry claim in the future.
    let httpParams = new HttpParams();
    httpParams = httpParams.append("claimId", claimId);
  }

  exportToCsv(dataTable: Table): void{
    const columns =
      [
        {field: 'sn', header: 'SN', index: 0},
        {field: 'receiptDt', header: 'Date Of Receipt', index: 1},
        {field: 'caseId', header: 'Case Id', index: 2},
        {field: 'claimStartDate', header: 'Claim Start', index: 3},
        {field: 'claimEndDate', header: 'Claim End', index: 4},
        {field: 'source', header: 'Source', index: 5},
        {field: 'company', header: 'Company', index: 6},
        {field: 'itemNames',  header: 'Item', index: 7},
        {field: 'lastVerifiedInitials', header: 'Verifier', index: 8},
        {field: 'claimStatus', header: 'Status', index: 9},
      ];

    const dtoList:any[] = [];

    let rowIndex = 0;

    this.viewPastClaims.forEach((viewPastClaim:ViewPastClaim, index:number) => {
      dtoList.push({
        sn: index + 1,
        receiptDt: this.dateUtilsService.convertUnixToDateString(viewPastClaim.receiptDt, "DD-MM-YYYY"),
        caseId: viewPastClaim.caseId,
        claimStartDate: viewPastClaim.claimStartDate,
        claimEndDate: viewPastClaim.claimEndDate,
        source: viewPastClaim.source,
        company: viewPastClaim.claimType === "S"? viewPastClaim.companyName : (viewPastClaim.claimType === "E"? viewPastClaim.csn.substring(0,10).concat("-").concat(viewPastClaim.csn.substring(10,13)).concat("-").concat(viewPastClaim.csn.substring(13,15)) : ""),
        itemNames: viewPastClaim.itemNames,
        lastVerifiedInitials: viewPastClaim.lastVerifiedInitials,
        claimStatus: viewPastClaim.claimStatus,
      })
      rowIndex++;
      dtoList.push({
        sn: '',
        receiptDt: '',
        caseId: '',
        claimStartDate: '',
        claimEndDate: '',
        source: '',
        company: viewPastClaim.claimType === "S"? viewPastClaim.companyName : (viewPastClaim.claimType === "E"? viewPastClaim.companyName : ""),
        itemNames: '',
        lastVerifiedInitials: '',
        claimStatus: '',
      })
      rowIndex++;

      if(viewPastClaim.claimType === "S"){
        dtoList.push({
          sn: '',
          receiptDt: '',
          caseId: '',
          claimStartDate: '',
          claimEndDate: '',
          source: '',
          company: viewPastClaim.occupationCode === "990"? viewPastClaim.occupationName.concat(" - ").concat(viewPastClaim.otherOccupationDesc): viewPastClaim.occupationName,
          itemNames: '',
          lastVerifiedInitials: '',
          claimStatus: '',
        })
        rowIndex++;
      }
    });

    this.exportUtilsService.exportCSV(dataTable, dtoList,rowIndex, columns,
      "view_past_claims".concat(formatDate(new Date(),"dd-MM-YYYY", this.localeId)));
  }

  filterCoy(dataTable: Table): void{
    if(this.checkBoxValue) {
      dataTable.filter(this.params.csn, 'csn', 'equals');
    } else {
      dataTable.reset();
    }
  }
}
<div class = "grid col-4" [style]="{'margin-bottom':'1em'}">
  <div class = "col">
      NRIC: {{ params.nric }}
  </div>
  <div class = "col">
      Name:  {{ params.name }}
  </div>
  <div class = "col">
      Case ID: {{ params.caseId }}
  </div>
</div>
<h6>Claim Summary <p-checkbox *ngIf = "this.params.coyName" [(ngModel)]="checkBoxValue" [binary]="true" (onChange)="filterCoy(dt)" label="Only Show For {{this.params.coyName}}" [style] = "{'margin-left':'1em'}"></p-checkbox></h6>
<hr>
<p-table #dt [value] = viewPastClaims
         styleClass="p-datatable-gridlines p-datatable-striped">
  <ng-template pTemplate="header">
    <tr>
      <th scope="col" colspan = "10">
        <p-button (click) = "exportToCsv(dt)" icon="pi pi-file-o"></p-button>
      </th>
    </tr>
    <tr>
      <th scope="col">SN</th>
      <th scope="col" pSortableColumn="receiptDt">
        Date Of Receipt <p-sortIcon field="receiptDt"></p-sortIcon>
      </th>
      <th scope="col" pSortableColumn="caseId">
        Case Id <p-sortIcon field="caseId"></p-sortIcon>
      </th>
      <th scope="col" pSortableColumn="claimStartDateForSorting">
        Claim Start <p-sortIcon field="claimStartDateForSorting"></p-sortIcon>
      </th>
      <th scope="col" pSortableColumn="claimEndDateForSorting">
        Claim End <p-sortIcon field="claimEndDateForSorting"></p-sortIcon>
      </th>
      <th scope="col" pSortableColumn="source">
        Source <p-sortIcon field="source"></p-sortIcon>
      </th>
      <th scope="col" pSortableColumn="companyName">
        Company <p-sortIcon field="companyName"></p-sortIcon>
      </th>
      <th scope="col" pSortableColumn="itemNames">
        Item <p-sortIcon field="itemNames"></p-sortIcon>
      </th>
      <th scope="col" pSortableColumn="lastVerifiedInitials">
        Verifier <p-sortIcon field="lastVerifiedInitials"></p-sortIcon>
      </th>
      <th scope="col" pSortableColumn="claimStatus">
        Status <p-sortIcon field="claimStatus"></p-sortIcon>
      </th>
    </tr>
  </ng-template>
  <ng-template pTemplate="body" let-pastClaim let-rowIndex = "rowIndex">
    <tr [ngStyle]= "{'background': pastClaim.csn === this.params.csn ? 'yellow' :'#ffffff'}">
      <td>{{rowIndex + 1}}</td>
      <td>
        <div>{{this.dateUtilsService.convertUnixToDateString(pastClaim.receiptDt, "DD-MM-YYYY")}}</div>
      </td>
      <td>
        <div><a href='javascript:' (click)="popUpEnquireClaim(pastClaim.claimId)">{{pastClaim.caseId}}</a></div>
      </td>
      <td>
        {{pastClaim.claimStartDate}}
      </td>
      <td>
        {{pastClaim.claimEndDate}}
      </td>
      <td>
        {{pastClaim.source}}
      </td>
      <td>
        <div *ngIf = "pastClaim.claimType === 'S'">{{pastClaim.claimOption}}</div>
        <div *ngIf = "pastClaim.claimType === 'E'">{{pastClaim.csn.substring(0,10)}}-{{pastClaim.csn.substring(10,13)}}-{{pastClaim.csn.substring(13,15)}}</div>
        <div>{{pastClaim.companyName}}</div>
        <div *ngIf = "pastClaim.claimType === 'S'">{{pastClaim.claimType === "S"? pastClaim.companyName : (pastClaim.claimType === "E"? pastClaim.csn.substring(0,10).concat("-").concat(pastClaim.csn.substring(10,13)).concat("-").concat(pastClaim.csn.substring(13,15)) : "")}}</div>
      </td>
      <td>
        {{pastClaim.itemNames}}
      </td>
      <td>
        {{pastClaim.lastVerifiedInitials}}
      </td>
      <td>
        {{pastClaim.claimStatus}}
      </td>
    </tr>
  </ng-template>
  <ng-template pTemplate="emptymessage">
    <tr>
      <td [attr.colspan] = "10">
            <span>
              <i class="pi pi-info-circle"></i>
              No records found.
            </span>
      </td>
    </tr>
  </ng-template>
</p-table>
