import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Title } from '@angular/platform-browser';
import { TranslateService } from '@ngx-translate/core';

@Component({
  selector: 'n2-bankfile-stop-payment',
  templateUrl: './bankfile-stop-payment.component.html',
  styleUrls: ['./bankfile-stop-payment.component.scss']
})
export class BankfileStopPaymentComponent implements OnInit {
  activeIndex = 0;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private titleService: Title,
    private translateService: TranslateService
  ) {
  }

  ngOnInit(): void {
    this.activeIndex = this.route.snapshot.data['tab'];
  }

  setTitle(newTitle: string): void {
    this.titleService.setTitle(newTitle);
  }

  changeTitleBasedOnSelectedTabs(): void {
    switch (this.activeIndex) {
      case 0: {
        this.translateService.get('nspay2App.payment.bankfile.home.title').subscribe(localizedtitle => {
          this.setTitle(localizedtitle);
        });
        break;
      }
      case 1: {
        this.translateService.get('nspay2App.payment.stopPayment.home.title').subscribe(localizedtitle => {
          this.setTitle(localizedtitle);
        });
        break;
      }
      case 2: {
        this.translateService.get('nspay2App.payment.instalmentBankfile.home.title').subscribe(localizedtitle => {
          this.setTitle(localizedtitle);
        });
        break;
      }
      default: {
        break;
      }
    }
  }
}
